  disableStartDateHandler = () => {
    if (this.state.startDate && this.state.endDate) {
      let todayDate = moment().format('YYYY-MM-DD');
      let editStartDate = moment(this.state.startDate.format('YYYY-MM-DD')).format('YYYY-MM-DD'),
        editEndDate = moment(this.state.startDate.format('YYYY-MM-DD'))
          .add(14, 'weeks')
          .format('YYYY-MM-DD'),
        editWeeksCheck = moment(this.state.endDate.format('YYYY-MM-DD')).isBetween(
          editStartDate,
          editEndDate
        );
      if (this.state.isEdit) {
        if (!this.state.editBy) {
          this.setState({ disableStartDate: true });
        }
        if (
          this.state.editBy &&
          this.state.startDate &&
          moment(this.state.startDate.format('YYYY-MM-DD')).isAfter(todayDate) &&
          this.state.endDate &&
          editWeeksCheck
        ) {
          this.setState({ disableStartDate: false });
        }
        if (
          this.state.editBy &&
          this.state.startDate &&
          moment(this.state.startDate.format('YYYY-MM-DD')).isBefore(todayDate) &&
          this.state.endDate &&
          editWeeksCheck
        ) {
          this.setState({ disableStartDate: true });
        }
      }
    }
  };
  getWeekYearMapping = () => {
    let week = [];
    let year = [];
    let endDate = moment(this.state.endDate.format('YYYY-MM-DD')).format('YYYY-MM-DD');
    let startDate = this.state.startDate.format('YYYY-MM-DD');

    while (moment(startDate).isBefore(endDate)) {
      week.push(moment(startDate).week());
      year.push(moment(startDate).year());

      let startWeekDate = moment(startDate).add(1, 'weeks').format('YYYY-MM-DD');
      startDate = startWeekDate;
    }
    return { week, year };
  };
  checkDateRange = () => {
    let todayDate = moment().subtract(1, 'weeks').format('YYYY-MM-DD'),
      endDate = moment().add(14, 'weeks').format('YYYY-MM-DD'), //user can select 13 weeks from today date
      weeksCheck = moment(this.state.endDate.format('YYYY-MM-DD')).isBetween(todayDate, endDate);
    let editStartDate = moment(this.state.startDate.format('YYYY-MM-DD')).format('YYYY-MM-DD'),
      editEndDate = moment(this.state.startDate.format('YYYY-MM-DD'))
        .add(14, 'weeks')
        .format('YYYY-MM-DD'),
      editWeeksCheck = moment(this.state.endDate.format('YYYY-MM-DD')).isBetween(
        editStartDate,
        editEndDate
      );
    if (
      this.state.startDate &&
      this.state.endDate &&
      moment().isBefore(this.state.startDate, 'week') &&
      weeksCheck
    ) {
      return true;
    }
    console.log(todayDate, 'todayDate', this.state.startDate, 'week');
    if (
      this.state.isEdit &&
      this.state.editBy &&
      this.state.startDate &&
      this.state.endDate &&
      editWeeksCheck
    ) {
      return true;
    }
    let msg = _.isEmpty(this.state.startDate)
      ? 'Please enter Start Date'
      : _.isEmpty(this.state.endDate)
      ? 'Please enter End Date'
      : 'Start week should be greater than current week and end date less than 13 weeks';
    toast.error(msg, {
      autoClose: 15000,
      hideProgressBar: true,
    });
    return false;
  };

