  getScenarioandSellthrough = () => {
    let dataObj = this.props.editPlanDetails[0] && this.props.editPlanDetails[0].request_obj;
    let scenario = Object.keys(dataObj.analysisData);
    let analysisDataObj = dataObj.analysisData[scenario[0]];
    let { calPastWeeks, calFutureWeeks } = this.props.weekInfo;
    let { pastWeeks, futureWeeks } = [];
    futureWeeks = calFutureWeeks.map((item) => {
      let dataDate =
        this.props.fiscalCalendarDetails &&
        this.props.fiscalCalendarDetails[item && item['yearNum']] &&
        this.props.fiscalCalendarDetails[item && item['yearNum']][item && item['weekNum']];
      return `W${dataDate.fw}`;
    });
    pastWeeks =
      calPastWeeks &&
      calPastWeeks.length &&
      calPastWeeks.map((item) => {
        let dataDate =
          this.props.fiscalCalendarDetails &&
          this.props.fiscalCalendarDetails[item && item['yearNum']] &&
          this.props.fiscalCalendarDetails[item && item['yearNum']][item && item['weekNum']];
        return `W${dataDate.fw}`;
      });
    let scenarioName = '';
    for (let item in dataObj.cadenceData) {
      scenarioName = item;
    }
    let array = this.state.editBy ? dataObj.cadenceData[scenarioName] : this.getCadenceData(0);
    let scenario_data_old = {},
      scenario_data_new = {},
      sell_through_old_overall = {};
    _.map(array, (arrayItem) => {
      //map the array to handle multiple bins
      let oldArry = {},
        newArry = {};
      _.map(Object.keys(arrayItem), (item, index) => {
        //map each bin(level)
        //store old weeks data and new weeks data in seperate objects
        let newData =
          this.props.daterangeOrWeek === 'date_range' ? arrayItem[item].val : arrayItem[item];
        if (pastWeeks && pastWeeks.includes(item)) {
          oldArry[item] = newData; //arrayItem[item];
          sell_through_old_overall[item] =
            dataObj.analysisData[scenario[0]].overview.sellThrough[index]; // sell though is bin/level independent
        }
        if (futureWeeks && futureWeeks.includes(item)) {
          newArry[item] = newData; //arrayItem[item];
        }
      });
      //store in object of key value pair format, where key will b bin data and value will be week data
      if (this.state.cadenceConstraintsVal.value === 'overall') {
        scenario_data_old[arrayItem.bins] = oldArry;
        scenario_data_new[arrayItem.bins] = newArry;
      }
      if (this.state.cadenceConstraintsVal.value === 'sku') {
        scenario_data_old[arrayItem.SKU_Id] = oldArry;
        scenario_data_new[arrayItem.SKU_Id] = newArry;
      }
    });
    let keys = Object.keys(scenario_data_old);
    let newKeys = Object.keys(scenario_data_old);
    let sell_through_old_sku = [];
    if (!_.isEmpty(Object.keys(scenario_data_old[keys[0]]))) {
      _.map(analysisDataObj.detailed.data, (item) => {
        let newObj = {};
        _.map(analysisDataObj.detailed.weeks, (weekNum) => {
          let newWeekNum = weekNum.replace('w', 'W');
          if (pastWeeks && pastWeeks.includes(newWeekNum)) {
            newObj[weekNum.replace('w', 'W')] = item[`${weekNum}sellThrough`];
          }
        });
        newObj.SKU_Id = item.SKU_Id;
        sell_through_old_sku.push(newObj);
      });
    }
    let scenarioandSellthrough = {};
    scenarioandSellthrough.scenario_data_old = _.isEmpty(Object.keys(scenario_data_old[keys[0]]))
      ? {}
      : scenario_data_old;
    scenarioandSellthrough.scenario_data_new = _.isEmpty(Object.keys(scenario_data_new[newKeys[0]]))
      ? {}
      : scenario_data_new;
    scenarioandSellthrough.sell_through_old_overall = _.isEmpty(
      Object.keys(sell_through_old_overall)
    )
      ? []
      : [sell_through_old_overall];
    scenarioandSellthrough.sell_through_old_sku = sell_through_old_sku;
    return scenarioandSellthrough;
  };
