  validateCadenceValue = () => {
    let index = this.state.TabTitles.indexOf(this.state.defaultActiveKey);
    let array = _.cloneDeep(this.getCadenceData(index));
    if (_.isEmpty(array) || _.isEmpty(array[0])) return false;
    for (let item of array) {
      let breakLoop = false;
      for (const prop in item) {
        let val = this.props.daterangeOrWeek === 'date_range' ? item[prop].val : item[prop];
        let isValid = val
          ? val >= parseInt(this.state.validateDiscount.min) &&
            val <= parseInt(this.state.validateDiscount.max) &&
            val % parseInt(this.state.validateDiscount.interval) == 0
          : false;

        if (prop.includes('W') && !isValid) {
          toast.error(
            `Please enter values between ${this.state.validateDiscount.min} and ${this.state.validateDiscount.max} in intervals of ${this.state.validateDiscount.interval}`,
            {
              autoClose: 15000,
              hideProgressBar: true,
            }
          );
          let newCadenceData = _.cloneDeep(this.state.simulatedCadenceData);
          if (this.state.cadenceConstraintsVal.value === 'overall') {
            this.setState({ overallCadenceData: newCadenceData, disableSimBtn: true });
          } else {
            this.setState({ skuLabelCadenceData: newCadenceData, disableSimBtn: true });
          }
          breakLoop = true;
          return;
        }
      }
      if (breakLoop) return false;
    }
    return true;
  };
