      analysisTableData[1].style_desc = (averageData.sumMargin / averageData.sumRevenue) * 100;
    }
    if (analysisTableData[2].style_id == 'AUR ($)') {
      analysisTableData[2].style_desc = averageData.sumRevenue / averageData.sumUnits;
    }
    if (analysisTableData[3].style_id == 'AUM ($)') {
      analysisTableData[3].style_desc = averageData.sumMargin / averageData.sumUnits;
    }
    if (analysisTableData[4].style_id == 'Revenue ($)') {
      analysisTableData[4].style_desc = averageData.sumRevenue;
    }
    if (analysisTableData[5].style_id == 'Margin ($)') {
      analysisTableData[5].style_desc = averageData.sumMargin;
    }
    if (analysisTableData[6].style_id == 'Units') {
      analysisTableData[6].style_desc = averageData.sumUnits;
    }
    if (analysisTableData[7].style_id == 'Planned Units') {
