            overview.margin.push(eachSku[key]);
          }
          if (key.includes('units')) {
            overview.units.push(eachSku[key]);
            sumUnits += Number(eachSku[key]);
          }
          if (key.includes('revenue')) {
            overview.revenue.push(eachSku[key]);
            sumRevenue += Number(eachSku[key]);
          }
          if (key.includes('sellThrough')) {
            overview.sellThrough.push(eachSku[key]);
          }
          if (key.includes('planned_units')) {
            overview.planned_units.push(eachSku[key]);
            sumPlannedunits += Number(eachSku[key]);
          }
        });
      }
    });
    average = {
      sumMargin: sumMargin,
      sumPlannedunits: sumPlannedunits,
      sumRevenue: sumRevenue,
      sumUnits: sumUnits,
      sellThrough: overview.sellThrough[overview.sellThrough.length - 1],
    };

    let analysisTableDatabySkus = this.updateAnalysisTableDatabySkus(average);
    return { overview, analysisTableDatabySkus };
  };

