          item.scenarios === 'Optimal' ? 0 : item.scenarios.charAt(item.scenarios.length - 1);
        delete scenios.scenarios;
        cadence[index] = [scenios];
      });
      let data = cadence[this.state.TabTitles.indexOf('Comparison')];
      for (let i = 1; i < data.length; i++) {
        let row = cloneDeep(data[i]);
        let scenarioName = row.scenarios;
        delete row.scenarios;
        let array = _.cloneDeep([row]);
        let newArray = {};
        let comArray = {};
        _.map(array, (item) => {
          if (this.props.daterangeOrWeek === 'date_range') {
            item = this.dateRangeCaseHandler(item);
          }
          let key1 = item.SKU_Id;
          delete item.SKU_Id;
          comArray[key1] = item;
          let key = item.bins;
          delete item.bins;
          newArray[key] = item;
        });

        let res = await this.props.simulate(
          {
            hierarchy1: this.props.selectedHierarchy1,
            hierarchy2: this.props.selectedHierarchy2,
