        //   }
        //   //case: reoptimzed
        //   else {
        //     this.fetchCadenceHandler(data.simulation_id);
        //   } // this.props.simulationId

        // return; // uncomment this if notification need to be updated when user is in create md screen
      }
      let newSimulationIdArray = this.props.simulationId;
      newSimulationIdArray && newSimulationIdArray.push(data.simulation_id);
      if (newSimulationIdArray && !newSimulationIdArray.includes(this.props.simulationId)) {
        this.props.setSimulationId(_.uniq(newSimulationIdArray));
