  createRooms = () => {
    let token = localStorage.getItem('token');
    console.log(token, 'token');
    this.socket.emit('create room', {
      token: token, //token stored
    });
  };
  fetchReoptimizeDataHandler = (id) => {
    if (!id) return;
    this.props.fetchReoptimizeData({ id: id }).then((res) => {
      let cadenceOutput = res[0] && res[0].output && res[0].output.simulateData;
      let abc = res[0] && res[0].output && res[0].output.cadenceData;
      this.setState(
        {
          overallCadenceData: cloneDeep([abc]),
          simulatedCadenceData: cloneDeep([cadenceOutput]), //reset edited input field data
          skuLabelCadenceData: cloneDeep([abc]),
          showCadence: res[0] && res[0].output && res[0].output.cadenceData ? true : false,
          showProcessing: false,
        },
        () => {
          this.props.disableSaveHandler && this.props.disableSaveHandler(false);
        }
      );
    });
  };
  fetchCadenceHandler = (id) => {
    if (!id && this.state.editBy) return;
    this.props.fetchCadence({ id: id }).then((res) => {
      if (!res || !res[0] || _.isEmpty(res[0].input)) return;
      let data = res[0].input;

      this.setState({
        startDate: moment(data.startDate),
        endDate: moment(data.endDate),
        weekNumList: data.week,
        marginVal: data.margin,
        targetSellThroughVal: data.sellThrough,
        cadenceConstraintsVal: {
          label: data.md_level && data.md_level.charAt(0).toUpperCase() + data.md_level.slice(1),
          value: data.md_level,
        },
        markdownTypeVal: { label: data.markdown_type, value: data.markdown_type },
        validateDiscount: {
          min: data.min_discount,
          max: data.max_discount,
          interval: data.interval_discount,
        },
        discount: {
          min: data.min_discount,
          max: data.max_discount,
          interval: data.interval_discount,
        },
        offerType:
          data.offer_value == 'pp' || data.offer_value == 'fixed_price'
            ? { label: 'PP', value: 'pp', key: 'fixed_price' }
            : { label: '%off', value: '%off', key: 'percent_off' },
        offerIcon: data.offer_value == 'pp' || data.offer_value == 'fixed_price' ? '$' : '%',
        offerIconText:
          data.offer_value == 'pp' || data.offer_value == 'fixed_price' ? 'price $' : 'discount %',
        mdFrequency: { min: data.min_md_frequency, max: data.max_md_frequency },
        noOfDistinctMd: { min: data.min_no_of_distinct_md, max: data.max_no_of_distinct_md },
        validateDistinctMD: { min: data.min_no_of_distinct_md, max: data.max_no_of_distinct_md },
        validateMDfrequency: { min: data.min_md_frequency, max: data.max_md_frequency },
        stepSize: { min: data.min_step_size, max: data.max_step_size },
        discountBins: data.no_of_discount_bins,
        discountDiff: { min: data.min_discount_difference, max: data.max_discount_difference },
        validateStepSize: { min: data.min_step_size, max: data.max_step_size },
        validateDiscountDiff: {
          min: data.min_discount_difference,
          max: data.max_discount_difference,
        },
        addConstraints: true,
      });

      let newfilters = _.cloneDeep(data.filters);
      let customStyleGroupName = [...newfilters['customStyleGroup']];
      newfilters['customStyleGroup'] = data.customStyleGroupId;
      delete newfilters['custom_style_group_info'];
      let payload = {
        filters: newfilters,
      };
      if (data.filters.channel === 'Web') payload.filters['ecom_flag'] = [1];
      if (data.filters.channel === 'Outlet') payload.filters['retail_flag'] = [1];
      this.setState({
        filteredData: payload,
        saveCadenceRequest: data,
        // excludedRows: [],
      });
      if (this.props.location.state && this.props.location.state.fromNotification) {
        let filtersData = getUpdatedFilters(
          this.props.filtersData,
          newfilters,
          customStyleGroupName
        );
        this.props.setFiltersData(filtersData, customStyleGroupName);
        this.props.fetchInventoryInfo(payload);
      }
      // this.props.fetchOfferDetails(payload);
    });
    this.props.location.state &&
      this.props.location.state.fromNotification &&
      this.setState({ fromNotification: true });
  };
  fetchEditPlanaDataHandler = (planId) => {
    this.props.fetchEditPlanData({ promo_code: planId }).then((res) => {
      if (!_.isEmpty(res) && !_.isEmpty(res[0]) && !_.isEmpty(res[0].request_obj)) {
        let editDetails = res[0].request_obj;
        this.setState({
          filteredData: { filters: editDetails.filters },
          startDate: moment(editDetails.startDate),
          endDate: moment(editDetails.endDate),
          totalInDc: editDetails.totalInDc,
          weekNumList: editDetails.week,
          totalOnHand: editDetails.totalOnHand,
          totalOnOrder: editDetails.totalOnOrder,
          totalInTransit: editDetails.totalInTransit,
          stores: editDetails.stores,
          totalStores: editDetails.stores && editDetails.stores.length,
