          },
          markdownTypeVal: { label: editDetails.markdown_type, value: editDetails.markdown_type },
          mdFrequency: { min: editDetails.min_md_frequency, max: editDetails.max_md_frequency },
          noOfDistinctMd: {
            min: editDetails.min_no_of_distinct_md,
            max: editDetails.max_no_of_distinct_md,
          },
          validateDistinctMD: {
            min: editDetails.min_no_of_distinct_md,
            max: editDetails.max_no_of_distinct_md,
          },
          validateMDfrequency: {
            min: editDetails.min_md_frequency,
            max: editDetails.max_md_frequency,
          },
          discountBins: editDetails.no_of_discount_bins,
          discount: {
            min: editDetails.min_discount,
            max: editDetails.max_discount,
            interval: editDetails.interval_discount,
          },
          validateDiscount: {
            min: editDetails.min_discount,
            max: editDetails.max_discount,
            interval: editDetails.interval_discount,
          },
          stepSize: { min: editDetails.min_step_size, max: editDetails.max_step_size },
          discountDiff: {
            min: editDetails.min_discount_difference,
            max: editDetails.max_discount_difference,
          },

          validateStepSize: { min: editDetails.min_step_size, max: editDetails.max_step_size },
          validateDiscountDiff: {
            min: editDetails.min_discount_difference,
            max: editDetails.max_discount_difference,
          },
          addConstraints: true, // so that it wont reset on constrint btn click
        });
        let filteredData = { filters: editDetails.filters };
        if (!editDetails.max_discount || !editDetails.interval_discount) {
          this.props.fetchOfferDetails(filteredData).then(() => {
            if (!editDetails.max_discount && !editDetails.interval_discount) {
              editDetails.offer_type == 'fixed_price'
                ? this.setState({
                    discount: this.props.discountPP,
                    validateDiscount: this.props.discountPP,
                  })
                : this.setState({
                    discount: this.props.discountOff,
                    validateDiscount: this.props.discountOff,
                  });
            }
            if (editDetails.max_discount && !editDetails.interval_discount) {
              editDetails.offer_type == 'fixed_price'
                ? this.setState({
                    discount: {
                      min: editDetails.min_discount,
                      max: editDetails.max_discount,
                      interval: this.props.discountPP.interval,
                    },
                    validateDiscount: {
                      min: editDetails.min_discount,
                      max: editDetails.max_discount,
                      interval: this.props.discountPP.interval,
                    },
                  })
                : this.setState({
                    discount: {
                      min: editDetails.min_discount,
                      max: editDetails.max_discount,
                      interval: this.props.discountOff.interval,
                    },
                    validateDiscount: {
                      min: editDetails.min_discount,
                      max: editDetails.max_discount,
                      interval: this.props.discountOff.interval,
                    },
                  });
            }
          });
        }

        let customStyleGroupName = [...editDetails.customStyleName];
        let filtersData = getUpdatedFilters(
          this.props.filtersData,
          filteredData.filters,
          customStyleGroupName
        );
        this.props.setFiltersData(filtersData, customStyleGroupName);
      }
    });
  };
  componentDidMount() {
    if (
      this.props.location.state &&
      this.props.location.state.fromNotification &&
      this.props.location.state.plan_id
    ) {
      this.fetchCadenceHandler(this.props.location.state.plan_id);
    }
    this.initSocketConnection();
    this.setupSocketListeners();
    // this.createRooms()

    if (this.props.location.state && this.props.location.state.planId) {
      let { isEdit, planId } = this.props.location && this.props.location.state;
      this.setState({
        isEdit,
        planId,
        editFrom: 'notEditModal', //redirect to create md screen case, not popup
        fromDraft:
          this.props.location && this.props.location.state && this.props.location.state.fromDraft,
      });
      this.fetchEditPlanaDataHandler(planId);
    }

    if (this.props.editModal && this.props.planId) {
      let { editModal, planId, from } = this.props;
      this.setState({ isEdit: editModal, planId, editFrom: from });
      this.fetchEditPlanaDataHandler(planId);
    }

    this.props.savedStatusFalse(false);
  }
  componentDidUpdate(prevProps, prevState) {
    if (
      this.props.location.state &&
      this.props.location.state.fromNotification &&
      this.props.location.state.plan_id &&
      ((prevProps.location.state &&
        this.props.location.state.plan_id !== prevProps.location.state.plan_id) ||
        (!prevProps.location.state && this.props.location.state.plan_id))
    ) {
      this.setState({
        isEdit: false,
        planId: null,
        editFrom: null,
        fromDraft: false,
        defaultActiveKey: 'Optimal',
        TabTitles: ['Optimal'],
      });
      this.fetchCadenceHandler(this.props.location.state.plan_id);
    }
    // Generate cadence on createMD flow
    if (SIMULATION_ID && !this.state.isEdit && !this.state.editBy) {
      this.fetchCadenceHandler(SIMULATION_ID);
      SIMULATION_ID = null;
    }
    // Generate cadence on reoptimize
    if (SIMULATION_ID && this.state.editBy) {
      this.fetchReoptimizeDataHandler(SIMULATION_ID);
      SIMULATION_ID = null;
    }
    if (this.props.savedStatus) {
      document.body.setAttribute('style', '');
      this.props.updateSidebar('/vis/Markdown/draftStatus');
      if (this.state.status === 'draft') {
        this.props.history.push('/vis/Markdown/draftStatus');
      } else {
        this.props.history.push({
          pathname: '/vis/Markdown/draftStatus',
          state: { tab: 'status' },
        });
      }
      return;
    }
    if (this.props.cadenceData !== prevProps.cadenceData) {
      this.setState({
        showCadence: true,
        simulatedData: true,
      });
      this.setState({
        overallCadenceData: _.cloneDeep(this.props.cadenceData),
        simulatedCadenceData: _.cloneDeep(this.props.cadenceData), //reset edited input field data
        skuLabelCadenceData: _.cloneDeep(this.props.cadenceData),
      });
      // this.state.cadenceConstraintsVal.value === 'overall' &&
      //   this.setState({
      //     overallCadenceData: cloneDeep(this.props.cadenceData),
      //     simulatedCadenceData: cloneDeep(this.props.cadenceData), //reset edited input field data
      //   });
      // this.state.cadenceConstraintsVal.value === 'sku' &&
      //   this.setState({
      //     skuLabelCadenceData: cloneDeep(this.props.cadenceData),
      //   });
    }
    if (!_.isEqual(prevProps.inventoryInfoData, this.props.inventoryInfoData)) {
      let storeIds = [];
      if (this.props.inventoryInfoData && this.props.inventoryInfoData.store_data) {
        storeIds = cloneDeep(this.props.inventoryInfoData.store_data).map((obj) => obj.store_code);
      }
      let totalInv = this.props.inventoryInfoData && this.props.inventoryInfoData.totalInv;
      let {
        inventory_in_dc,
        inventory_in_transit,
        inventory_on_hand,
        inventory_on_order,
      } = totalInv;
      let totalStores = this.props.inventoryInfoData && this.props.inventoryInfoData.totalStore;
      this.setState({
        stores: storeIds,
        totalInventory: totalInv,
        currentInventory: totalInv,
        selectedStores: totalStores,
        totalStores: totalStores,
        actualInv: totalInv,
        totalInDc: inventory_in_dc < 0 ? 0 : inventory_in_dc,
        totalOnHand: inventory_on_hand || 0,
        totalOnOrder: inventory_on_order || 0,
        totalInTransit: inventory_in_transit || 0,
      });
    }
    let editDetails = this.props.editPlanDetails[0] && this.props.editPlanDetails[0].request_obj;
    if (this.props.isSaveChanges) {
      this.props.isSaveChangesHandler(false);
      let scenarioName = '';
      let cadData = editDetails.cadenceData;
      for (let item in cadData) {
        scenarioName = item;
      }
      // this.onSaveEdit(null, [{ label: 'Optimal', value: 'Optimal' }]);
      this.onSaveEdit(editDetails.title, [{ label: scenarioName, value: scenarioName }]);
    }
  }
  componentWillUnmount() {
    this.props.onResetEditedInfo();
    this.props.onResetInventoryInfo();
  }
  disabledButtonHandler = (from) => {
    let {
      overallCadenceData,
      skuLabelCadenceData,
      defaultActiveKey,
      TabTitles,
      cadenceConstraintsVal,
    } = this.state;
    if (from === 'edit' && this.state.isDraftDisabled) {
      return true;
    }
    if (from === 'edit') {
      return _.isEmpty(this.props.editMarkdownAnalysisGraphData) ? true : false;
    }
    if (defaultActiveKey !== 'Comparison') {
      if (defaultActiveKey === 'Optimal') return false;
      if (!TabTitles.includes('Comparison')) return true;
      if (TabTitles.includes('Comparison')) {
        let tabs =
          cadenceConstraintsVal.value === 'overall'
            ? _.map(overallCadenceData[overallCadenceData.length - 1], (item) => item.scenarios)
            : _.map(skuLabelCadenceData[skuLabelCadenceData.length - 1], (item) => item.scenarios);

        return tabs.includes(defaultActiveKey) && !this.state.isDraftDisabled ? false : true;
      }
    }
    return from === 'approved' && this.state.isDraftDisabled
      ? true
      : from === 'approved'
      ? !this.state.comparisonRow.length >= 1
      : from === 'draft' && this.state.isDraftDisabled
      ? true
      : this.state.comparisonRow.length <= 0;
  };
  onSaveEdit = (planName, sceniosNames) => {
    let index = this.getActiveIndex();
    let editDetails = this.props.editPlanDetails[0] && this.props.editPlanDetails[0].request_obj;
    let startWeek = editDetails.startWeek;
    let endWeek = editDetails.endWeek;
    let weeks = editDetails.week;
    // this.props.daterangeOrWeek === 'date_range'
    //   ? this.state.weekNumList
    //   : this.getWeekRange(startWeek, endWeek);
    let selectedCustomStyleName = editDetails && editDetails.customStyleName;
    let custom_style_group_info = editDetails && editDetails.custom_style_group_info;

    let obj = {
      filters: editDetails.filters,
      customStyleName: selectedCustomStyleName,
      custom_style_group_info,
      title: editDetails.title,
      status: this.state.status,
      promo_code: this.state.planId,
      inventorySelected: '',
      storeSelected: '',
      sellThrough: editDetails.sellThrough,
      margin: editDetails.margin || 0,
      markdown_type: editDetails.markdown_type,
      cadence_type: this.state.cadenceConstraintsVal.value || 'overall',
      // cadenceData: this.props.editAnalysisData,
      startDate: editDetails.startDate,
      endDate: editDetails.endDate,
      week: weeks,
      totalInDc: editDetails.totalInDc,
      totalOnHand: editDetails.totalOnHand,
      totalOnOrder: editDetails.totalOnOrder,
      totalInTransit: editDetails.totalInTransit,
      stores: editDetails.stores,
      md_level: editDetails.md_level,
      md_level_no_range: editDetails.md_level_no_range,
      startWeek,
      endWeek,
      simulatedData: {},
      cadenceData: {},
      analysisData: {},
      csv_names: editDetails.csv_names, //this.props.csv_names,
      roomId: editDetails.roomId,
      userId: editDetails.userId,
      skus: this.props.skuData,
      save_as_default: editDetails.save_as_default,
      offer_type: editDetails.offer_type,
      min_md_frequency: editDetails.min_md_frequency,
      max_md_frequency: editDetails.max_md_frequency,
      min_no_of_distinct_md: editDetails.min_no_of_distinct_md,
      max_no_of_distinct_md: editDetails.max_no_of_distinct_md,
      no_of_discount_bins: editDetails.no_of_discount_bins,
      min_discount: editDetails.min_discount,
      max_discount: editDetails.max_discount,
      min_step_size: editDetails.min_step_size,
      max_step_size: editDetails.max_step_size,
      min_discount_difference: editDetails.min_discount_difference,
      max_discount_difference: editDetails.max_discount_difference,
    };
    if (this.state.defaultActiveKey === 'Comparison') {
      sceniosNames.forEach((sceniosObject) => {
        let temp = sceniosObject.value || sceniosObject.scenarios;
        obj.simulatedData[temp] = this.props.analysisData[sceniosObject.scenarios]
          ? this.props.analysisData[sceniosObject.scenarios].saveData
          : [];
        if (sceniosObject.scenarios === 'Optimal') {
          obj.analysisData[temp] = this.props.analysisData[sceniosObject.scenarios]
            ? this.props.analysisData[sceniosObject.scenarios].simulateData
            : [];
        } else {
          const { analysisTabledata, detailed, overview } = this.props.analysisData[
            sceniosObject.scenarios
          ];
          obj.analysisData[temp] = { analysisTabledata, detailed, overview };
        }
        delete sceniosObject.scenarios;
        delete sceniosObject.label;
        delete sceniosObject.value;
        obj.cadenceData[temp] = sceniosNames;
      });
    } else {
      let key = sceniosNames[0].value || this.state.defaultActiveKey;
      obj.simulatedData[key] = this.props.analysisData[this.state.defaultActiveKey]
        ? this.props.analysisData[this.state.defaultActiveKey].saveData
        : [];
      if (this.state.defaultActiveKey === 'Optimal') {
        obj.analysisData[key] = this.props.editMarkdownAnalysisGraphData;
      } else {
        const { analysisTabledata, detailed, overview } = this.props.analysisData[
          this.state.defaultActiveKey
        ];
        obj.analysisData[key] = { analysisTabledata, detailed, overview };
      }
      let data =
        this.state.cadenceConstraintsVal.value === 'overall'
          ? this.state.overallCadenceData[index]
          : this.state.skuLabelCadenceData[index];
      obj.cadenceData[key] = data;
    }
    this.setState({
      showPlanSavePopup: false,
    });
    this.props.saveData(obj);
  };
  onSave = (planName, sceniosNames) => {
    let index = this.getActiveIndex();
    let editDetails = this.props.editPlanDetails[0] && this.props.editPlanDetails[0].request_obj;
    let { editBy } = this.state;
    let { csv_names, reoptimizedData, skuData, analysisData } = this.props;
    let obj = cloneDeep(this.state.saveCadenceRequest);
    // obj.filters['customStyleGroup'] = this.props.selectedCustomStyleGroup;
    obj.filters = _.cloneDeep(this.state.filteredData && this.state.filteredData.filters);
    // delete obj.filters['custom_style_group_info'];
    delete obj.offer_value;
    delete obj.customStyleGroupId;
    obj.offer_type = this.state.offerType.key;
    obj.custom_style_group_info = this.state.fromNotification
      ? this.props.cadenceInput &&
        this.props.cadenceInput.filters &&
        this.props.cadenceInput.filters.custom_style_group_info
      : this.props.customStyleDetails;
    obj.title = editBy ? editDetails.title : planName;
    obj.status = this.state.status;
    obj.cadenceData = {};
    obj.simulatedData = {};
    obj.sellThrough = this.state.targetSellThroughVal;
    obj.analysisData = {};
    obj.csv_names = editBy ? reoptimizedData && reoptimizedData.csv_names : csv_names;
    obj.skus = editBy ? reoptimizedData && reoptimizedData.skus : skuData;
    obj.margin = this.state.marginVal || 0;
    obj.customStyleName = this.props.selectedCustomStyleName;

    if (this.state.defaultActiveKey === 'Comparison') {
      sceniosNames.forEach((sceniosObject) => {
        let temp = sceniosObject.value || sceniosObject.scenarios;
        obj.simulatedData[temp] = analysisData[sceniosObject.scenarios]
          ? analysisData[sceniosObject.scenarios].saveData
          : [];
        if (sceniosObject.scenarios === 'Optimal') {
