              </h6>
              <hr />
            </div>
            {this.state.showExcludeStorePopup && this.props.inventoryInfoData && (
              <div className="ai-pre-grid-table overflow-handler">
                <ExcludeStoreTable
                  currentPage={this.state.currentPage}
                  offset={this.state.offset}
                  showTablePopup={this.showTablePopup}
                  columnHeaders={this.props.inventoryInfoData.columnHeaders || []}
                  excludeStoreValHandler={this.excludeStoreValHandler}
                  storeData={this.props.inventoryInfoData.store_data || []}
                  excludedRows={this.state.excludedRows}
                />
              </div>
            )}
            {/* </div> */}
          </Modal>
          {this.state.showConstaints ? (
            <ConstraintsModal
              disableInReoptimize={this.state.isEdit && this.state.editBy}
              offerTypeHandler={this.offerTypeHandler}
              showConstaintsHandler={this.showConstaintsHandler}
              mdLevelHandler={this.mdLevelHandler}
              markdownTypeHandler={this.markdownTypeHandler}
              // stepSizeHandler={this.stepSizeHandler}
              // discountDiffHandler={this.discountDiffHandler}
              saveAsDefaultHandler={this.saveAsDefaultHandler}
              offerType={this.state.offerType}
              mdLevel={this.state.cadenceConstraintsVal}
              markdownTypeVal={this.state.markdownTypeVal}
              mdFrequency={this.state.mdFrequency}
              noOfDistinctMd={this.state.noOfDistinctMd}
              validateMDfrequency={this.state.validateMDfrequency}
              validateDistinctMD={this.state.validateDistinctMD}
              discountBins={this.state.discountBins}
              saveAsDefault={this.state.saveAsDefault}
              discount={this.state.discount}
              validateDiscount={this.state.validateDiscount}
              stepSize={this.state.stepSize}
              discountDiff={this.state.discountDiff}
              validateStepSize={this.state.validateStepSize}
              validateDiscountDiff={this.state.validateDiscountDiff}
              offerIcon={this.state.offerIcon}
              offerIconText={this.state.offerIconText}
              constraintHandler={this.constraintHandler}
              addConstraintsHandler={this.addConstraintsHandler}
            />
          ) : null}
        </div>
      </LoadingOverlay>
    );
  }
}
