import axios from 'axios';
import { config } from '../utils/config';

const filterActions = {
  getDrops: () => {
    return (dispatch) => {
      axios({
        url: `${config.createMarkdownURL}/getSeasonFilters`,
        method: 'GET',
      })
        .then((resp) => {
          dispatch({
            type: 'SET_DROPS',
            payload: resp.data.data,
          });
        })
        .catch((err) => {
          console.log(err);
        });
    };
  },

  getHierarchyLevel1: () => {
    return (dispatch) => {
      axios({
        url: `${config.createMarkdownURL}/getDepartmentFilters`,
        method: 'GET',
      })
        .then((resp) => {
          dispatch({
            type: 'SET_HIERARCHY1',
            payload: resp.data.data,
          });
        })
        .catch((err) => {
          console.log(err);
        });
    };
  },

  getHierarchyLevel2: (data) => {
    return (dispatch) => {
      axios({
        url: `${config.createMarkdownURL}/getHierarchyFilterData/`,
        method: 'POST',
        data: {
          filters: data,
          hierarchyType: 'HIERARCHY2',
        },
      })
        .then((resp) => {
          dispatch({
            type: 'SET_HIERARCHY2',
            payload: resp.data.data,
          });
        })
        .catch((err) => {
          console.log(err);
        });
    };
  },
  getHierarchyLevel3: (data) => {
    return (dispatch) => {
      axios({
        url: `${config.createMarkdownURL}/getHierarchyFilterData/`,
        method: 'POST',
