          filters: data,
          hierarchyType: 'HIERARCHY3',
        },
      })
        .then((resp) => {
          dispatch({
            type: 'SET_HIERARCHY3',
            payload: resp.data.data,
          });
        })
        .catch((err) => {
          console.log(err);
        });
    };
  },
  getHierarchyLevel4: (data) => {
    return (dispatch) => {
      axios({
        url: `${config.createMarkdownURL}/getHierarchyFilterData/`,
        method: 'POST',
        data: {
          filters: data,
          hierarchyType: 'HIERARCHY4',
        },
      })
        .then((resp) => {
          dispatch({
            type: 'SET_HIERARCHY4',
            payload: resp.data.data,
          });
        })
        .catch((err) => {
          console.log(err);
        });
    };
  },
  getHierarchyLevel5: (data) => {
    return (dispatch) => {
      axios({
        url: `${config.createMarkdownURL}/getHierarchyFilterData/`,
        method: 'POST',
        data: {
          filters: data,
          hierarchyType: 'HIERARCHY5',
        },
      })
        .then((resp) => {
          dispatch({
            type: 'SET_HIERARCHY5',
            payload: resp.data.data,
          });
        })
        .catch((err) => {
          console.log(err);
        });
    };
  },
  getHierarchyLevel6: (data) => {
    return (dispatch) => {
      axios({
        url: `${config.createMarkdownURL}/getHierarchyFilterData/`,
        method: 'POST',
        data: {
          filters: data,
          hierarchyType: 'HIERARCHY6',
        },
      })
        .then((resp) => {
          dispatch({
            type: 'SET_HIERARCHY6',
            payload: resp.data.data,
          });
        })
        .catch((err) => {
          console.log(err);
        });
    };
  },
  setSelectedDrops: (data) => {
    return (dispatch) => {
      dispatch({
        type: 'SET_SELECTED_DROPS',
        payload: data,
      });
    };
  },
  setSelectedHierarchy1: (data) => {
    return (dispatch) => {
      dispatch({
        type: 'SET_SELECTED_HIERARCHY1',
        payload: data,
      });
    };
  },
  setSelectedHierarchy2: (data) => {
    return (dispatch) => {
      dispatch({
        type: 'SET_SELECTED_HIERARCHY2',
        payload: data,
      });
    };
  },
  setSelectedHierarchy3: (data) => {
    return (dispatch) => {
      dispatch({
        type: 'SET_SELECTED_HIERARCHY3',
        payload: data,
      });
    };
  },
  setSelectedHierarchy4: (data) => {
    return (dispatch) => {
      dispatch({
        type: 'SET_SELECTED_HIERARCHY4',
        payload: data,
      });
    };
  },
  setSelectedHierarchy5: (data) => {
    return (dispatch) => {
      dispatch({
        type: 'SET_SELECTED_HIERARCHY5',
        payload: data,
      });
    };
  },
  setSelectedHierarchy6: (data) => {
    return (dispatch) => {
      dispatch({
        type: 'SET_SELECTED_HIERARCHY6',
        payload: data,
      });
    };
  },
  setSelectedHierarchy7: (data) => {
    return (dispatch) => {
      dispatch({
        type: 'SET_SELECTED_HIERARCHY7',
        payload: data,
      });
    };
  },
  resetFilters: () => {
    return (dispatch) => {
      dispatch({
        type: 'RESET_FILTERS',
      });
    };
  },
};

export default filterActions;
