import React from 'react';
import Modal from 'react-awesome-modal';
import Select from 'react-select';
import _ from 'lodash';

let mdTypeOptions = [
  { label: 'Hard MD', value: 'Hard MD' },
  { label: 'POS MD', value: 'POS MD' },
];
let mdLevelOptions = [
  { label: 'Overall', value: 'overall' },
  { label: 'SKU', value: 'sku' },
];
let offerTypeOptions = [
  { label: '%off', value: '%off', key: 'percent_off' },
  { label: 'PP', value: 'pp', key: 'fixed_price' },
];

const GetComparisonHeader = (props) => {
  return (
    <div className="constraints-modal">
      <Modal
        visible={true}
        effect="fadeInDown"
        width="35%"
        height="100%"
        onClickAway={() => {
          props.showConstaintsHandler(false);
        }}>
        {document.body.setAttribute('style', `position:fixed`)}
        <div className="card-body pb-0 px-0">
          <h6 className="card-title pb-4 pt-2 px-4 card-child" style={{ fontWeight: 'bold' }}>
            Add Additional Constraints
            <span
              onClick={() => props.showConstaintsHandler(false)}
              className="pull-right select-store-span">
              <i
                style={{ cursor: 'pointer' }}
                className="fa fa-times"
                onClick={() => props.showConstaintsHandler(false)}
                aria-hidden="true"
              />
            </span>
          </h6>
          <div className="constraint-scroll">
            <div className="d-inline-flex ">
              <div>
                <label className=" input-label mt-4 mx-4">Offer Type</label>
                <Select
                  className="md-level-offer-dropdown"
                  placeholder={false}
                  // defaultValue={{ label: 2002, value: 2002 }}
                  value={props.offerType}
                  options={offerTypeOptions}
                  onChange={props.offerTypeHandler}
                  //   isClearable
                  isDisabled={props.disableInReoptimize}  
                />
              </div>
              <div>
                <label className="input-label mt-4 mx-4">MD Level</label>
                <Select
                  className="md-level-offer-dropdown"
                  value={props.mdLevel}
                  options={mdLevelOptions}
                  onChange={props.mdLevelHandler}
                  //   isClearable
                  isDisabled={props.disableInReoptimize}
                />
              </div>
            </div>
            <hr />

            <div className="row mt-4 mx-4 ">
              <div className="col-5">
                <label className="input-label">Markdown type</label>
              </div>
              <div className="col-6">
                <Select
                  placeholder="Select"
                  value={props.markdownTypeVal}
                  options={mdTypeOptions}
                  onChange={props.markdownTypeHandler}
                  //   isClearable
                />
              </div>
            </div>
            <div className="row mt-4 mx-4 ">
              <div className="col-5">
                <label className="input-label d-inline">MD Frequency</label>
              </div>
              <div className="col-6 pr-0 row">
                <div className="col-6 pr-0">
                  <input
