import {
  GET_MARKDOWN_GLOBAL_PROMO_DATA,
  SAVE_GLOBAL_PROMO,
  GET_ERRORS,
  GET_MARKDOWN_GLOBAL_PROMO_DATA_PER_MARKDOWN,
  LOADER
} from "../../actions/types";

const initialState = {
  globalPromoData: [],
  globalPromoPerMarkDownData: [],
  error: null,
  loader: false
};

export default function(state = initialState, action) {
  switch (action.type) {
    case GET_MARKDOWN_GLOBAL_PROMO_DATA:
      return {
        ...state,
        globalPromoData: action.payload
      };
    case GET_MARKDOWN_GLOBAL_PROMO_DATA_PER_MARKDOWN:
      return {
        ...state,
        globalPromoPerMarkDownData: action.payload
      };
    case SAVE_GLOBAL_PROMO:
      return {
        ...state
      };
    case LOADER:
      return {
        ...state,
        loader: action.payload
      };
    case GET_ERRORS:
      return {
        ...state,
        globalPromoData: [],
        globalPromoPerMarkDownData: [],
        error: action.payload
      };
    default:
      return state;
  }
}
