import React from 'react'
import '../../custom-card.css';
import {Row,Col} from 'react-bootstrap'
import  ReactTooltip  from 'react-tooltip';

class PopUp extends React.Component{
    // eslint-disable-next-line no-useless-constructor
    constructor(props){
        super(props)
        this.state={
            graphs:[{
                name:'bar-chart',active:0
            },{
                name:'pie-chart',active:0
            },{
                name:'area-chart',active:0
            },{
                name:'line-chart',active:0
            },{
                name:'table',active:0
            },{
                name:'circle-o',active:0
            }]
        }
    }

    componentDidMount(){
       var graphdata = this.state.graphs;
       (this.props.graphs).forEach((item)=>{
           graphdata.forEach((elem,index)=>{
               if(elem.name===item){
                   elem.active=1
               }
           })
       })
       this.setState({
           graphs:graphdata
       })
    }
    render(){
        var data = this.props.data
        var graphs = this.state.graphs
        return(

            <div className="graph-popover-dropdown" style={{width:'250px',
                                      height:'305px',
                                      border: '1px solid  #A0A0A0',
                                      borderRadius:'4px',position:'absolute',
                                      zIndex:999,right:'3%',boxShadow:'0 0 4px 0',
                                      background:'white',fontSize:'14px',
                                      fontWeight:'bold',
                                      textAlign: "left",
                                      right: "18px",
                                      top: "15px" }}>
            {
                data.map(function(item,index){
                    if(item==='zoom'){
                        var icon = 'fa-search'
                    }
                    else if(item==='download'){
                        // eslint-disable-next-line no-redeclare
                        var icon = 'fa-download'
                    }
                    else{
                        // eslint-disable-next-line no-redeclare
                        var icon = 'fa-envelope'
                    }
                    return(
                        <Row>
                            <Col md={12}>
                                <div style={{marginRight:'10px',marginLeft:'10px',marginTop:(index===0)?'10px':''}}>
                                    <span style={{textTransform:'capitalize'}}>{item}</span>
                                    <i style={{float:'right'}} className={"fa "+icon+" popup"} aria-hidden="true"></i>
                                    <hr style={{marginTop:'8px',marginBottom:'10px'}}/>
                                </div>
                            </Col>

                        </Row>
                    )
                })
            }
            <Row><span style={{marginLeft:'25px'}}>View As:</span></Row>
            <Row style={{marginLeft:'10px'}}>
                {
                    graphs.map((item,index)=>{
                        return(
                            <Col md={4} style={{marginTop:'10px'}}>
                                <i onClick={()=>{this.props.onSelect(this.props.id,item.name);this.props.showPop(this.props.id)}} data-tip data-for={item.name} style={{color:(item.active)?'#007AFF':'#b4bab3',cursor:(item.active)?'pointer':'not-allowed'}} className={"fa fa-"+item.name+ " fa-2x"} aria-hidden="true" title="graphs" ></i>
                                <ReactTooltip id={item.name} type='dark' effect="float">
                                    <span style={{textTransform:'capitalize'}}>{item.name}</span>
                                </ReactTooltip>
                            </Col>
                        )
                    })
                }

            </Row>
            </div>

            // <div className="popup" style={{width:'250px',height:'auto',border: '1px solid  #A0A0A0',borderRadius:'4px',position:'absolute',zIndex:999,right:'3%',boxShadow:'0 0 4px 0',background:'white' }}>
            //     <Row>
            //         <Col md={12}>
            //             <div style={{marginRight:'10px',marginLeft:'10px',marginTop:'10px'}}>
            //                 <span>Zoom</span>
            //                 <i style={{float:'right'}} className="fa fa-search" aria-hidden="true"></i>
            //                 <hr/>
            //             </div>
            //         </Col>
            //     </Row>
            //     <Row>
            //         <Col md={12}>
            //         <div style={{marginRight:'10px',marginLeft:'10px'}}>
            //             <span>Download</span>
            //             <i style={{float:'right'}} className="fa fa-download" aria-hidden="true"></i>
            //             <hr/>
            //         </div>
            //         </Col>
            //     </Row>
            //     <Row>
            //         <Col md={12}>
            //         <div style={{marginRight:'10px',marginLeft:'10px'}}>
            //             <span>Email to</span>
            //             <i style={{float:'right'}} className="fa fa-envelope" aria-hidden="true"></i>
            //             <hr/>
            //         </div>
            //         </Col>
            //     </Row>
            //     <Row>
            //         <Col md={12}>
            //         <span style={{marginLeft:'10px'}}>View As:</span><br/>
            //         </Col>
            //     </Row>
            //     {/* <Row style={{marginLeft:'10px'}}>

            //             <Col md={4}>
            //                 <i className="fa fa-bar-chart fa-2x" aria-hidden="true" ></i>
            //             </Col>
            //             <Col md={4}>
            //                 <i className="fa fa-line-chart fa-2x" aria-hidden="true" ></i>
            //             </Col>
            //             <Col md={4}>
            //                 <i className="fa fa-pie-chart fa-2x" aria-hidden="true" ></i>
            //             </Col>
            //             <Col md={4}>
            //                 <i className="fa fa-area-chart fa-2x" aria-hidden="true" ></i>
            //             </Col>

            //     </Row> */}
            // </div>

        )
    }
}

class GraphPopOver extends React.Component{
    constructor(props){
        super(props)
        this.state={
            show:false
        }
        this.renderPop=this.renderPop.bind(this)
        this.handleClickOutside = this.handleClickOutside.bind(this)
    }

    componentDidMount() {
        document.addEventListener('mousedown', this.handleClickOutside);
    }

    handleClickOutside(event) {

        if(event.target.closest(".graph-popover-dropdown") && 
           event.target.closest(".graph-popover-dropdown").className && 
           event.target.closest(".graph-popover-dropdown").className == "graph-popover-dropdown"){
            return
        }

        if(event.target.title==='More'||event.target.title==='graphs'){
            return
        }

        this.setState({
            show:false
        })
      }


    renderPop(){
        this.setState({
            show:!this.state.show
        })
    }
    render(){
        var toShow=this.state.show
       return(
        <div style={{
            textAlign: "right",
            marginRight: "20px",
            marginTop: "5px",
            display: (this.props.inline ? 'inline' : 'block')
        }}>
            <i
                onClick={() => this.renderPop()}
                className="fa fa-ellipsis-h"
                title="More"
                style={{
                    fontSize: "20px",
                    color: (toShow)?"#2d65fc":"black",
                    marginTop: "5px",
                    cursor:'pointer',
                }}
            />
            {
                (toShow && this.props.data)?
                    <PopUp data={this.props.data} id={this.props.id} showPop={this.renderPop} onSelect={this.props.displayModal} graphs={this.props.graphs}/> :null
            }
        </div>

        )
    }

}

export default GraphPopOver
