        payload: data,
      });
    };
  },
  presetFilters: (data) => {
    return (dispatch) => {
      _.map(data, (item, index) => {
        dispatch({
          type: index === 8 ? 'SET_SELECTED_STORES' : `SET_SELECTED_HIERARCHY${index + 1}`,
          payload: item,
