import React, { Component } from 'react';
import Filters from '../../../services/filters';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import { cloneDeep, differenceBy } from 'lodash';
import _ from 'lodash';
import { toast } from 'react-toastify';

import '../../Configuration/StoreConfigContainers/StoreConfig.css';
import DataTable from '../../../services/reactTable';
// import StyleConfigFilter from '../StyleConfigFilter/StyleConfigFilter';
// import excelIcon from '../../../images/leftPanel/status.svg';
import StyleConfigActions from '../../../actions/styleConfigActions';

export class CreateStyleGroup extends Component {
  state = {
    groupName: '',
    allStyles: [],
    currentStyles: [],
    selectedAllStyles: [],
    selectedCurrentStyles: [],
    showEDLP: true,
    showGeo: false,
    header: [
      {
        Header: 'Style Color',
        accessor: 'id',
      },
      {
        Header: 'Styles Description',
        accessor: 'name',
      },
    ],
  };

  async componentDidMount() {
    if (this.props.styleGroupEdit) {
      this.onFilter();
      this.setState((state, props) => {
        return {
          currentStyles: props.styleConfigTableData[0] ? props.styleConfigTableData[0].subRows : [],
          groupName: props.styleConfigTableData[0] ? props.styleConfigTableData[0].style_group : [],
        };
      });
    }
  }

  onGroupNameChange = (e) => {
    this.setState({
      groupName: e.target.value,
    });
  };
  mandatoryFiltersVal = () => {
    return (
      this.props.selectedHierarchy1 &&
      this.props.selectedHierarchy2.length &&
      this.props.selectedHierarchy3.length &&
      this.props.selectedHierarchy4.length &&
      this.props.selectedHierarchy5.length &&
      this.props.selectedHierarchy6.length
    );
  };
  onFilter = () => {
    if (!this.mandatoryFiltersVal()) {
      toast.error(`Please select mandatory filters`, {
        autoClose: 15000,
        hideProgressBar: true,
      });
      return;
    }
    let req = {
      filters: {
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
        hierarchy5: this.props.selectedHierarchy5,
        hierarchy6: this.props.selectedHierarchy6,
      },
      hierarchyType: 'HIERARCHY7',
      allowInactiveProducts: false,
    };
    this.props.fetchStyle(req).then((res) => {
      this.setState({ allStyles: res });
    });
  };
  onAddClickHandler = () => {
    const allStyles = cloneDeep(this.state.allStyles);
    const currentStyles = cloneDeep(this.state.currentStyles);
    let newcurrentStyles = _.unionWith(currentStyles, this.state.selectedAllStyles, _.isEqual);

    let mainArray = allStyles,
      childArray = this.state.selectedAllStyles;

    if (allStyles.length < this.state.selectedAllStyles.length) {
      mainArray = this.state.selectedAllStyles;
      childArray = allStyles;
    }
    let newAllStyles = differenceBy(mainArray, childArray, 'id');
    this.setState({
      allStyles: newAllStyles,
      currentStyles: newcurrentStyles,
      selectedAllStyles: [],
    });
  };

  onRemoveClickHandler = () => {
    const currentStyles = cloneDeep(this.state.currentStyles);
    const allStyles = cloneDeep(this.state.allStyles);
    let mainArray = currentStyles,
      childArray = this.state.selectedCurrentStyles;

    if (currentStyles.length < this.state.selectedCurrentStyles.length) {
      mainArray = this.state.selectedCurrentStyles;
      childArray = currentStyles;
    }
    const newCurrentStyles = differenceBy(mainArray, childArray, 'id');
    // allStyles.push(...this.state.selectedCurrentStyles);
    let newallStyles = _.unionWith(allStyles, this.state.selectedCurrentStyles, _.isEqual);

    this.setState({
      allStyles: newallStyles,
      currentStyles: newCurrentStyles,
      selectedCurrentStyles: [],
    });
  };

  onAllStyleSelectionChangeHandler = (rowData) => {
    let { selectedAllStyles } = this.state;
    let foundEle = _.find(selectedAllStyles, { id: rowData.original.id });
    if (foundEle) {
      _.remove(selectedAllStyles, (item) => item.id === rowData.original.id);
    } else {
      selectedAllStyles.push(rowData.original);
    }
    this.setState({
      selectedAllStyles,
    });
  };

  onCurrentStylesSelectionChangeHandler = (rowData) => {
    let { selectedCurrentStyles } = this.state;
    let foundEle = _.find(selectedCurrentStyles, {
      id: rowData.original.id,
    });
    if (foundEle) {
      _.remove(selectedCurrentStyles, (item) => item.id === rowData.original.id);
    } else {
      selectedCurrentStyles.push(rowData.original);
    }
    this.setState({
      selectedCurrentStyles,
    });
  };

  onCreateStyleGroup = () => {
    if (this.props.styleGroupEdit) {
      let { groupName, currentStyles } = this.state;
      let styleCodes = [];
      currentStyles.map((ele) => styleCodes.push(ele.id));

      this.props.editStyleGroup({
        style_config_name: groupName,
        style_codes: styleCodes,
        style_type: 'group',
        style_config_id: groupName,
      });
    } else {
      let styleCodes = this.state.currentStyles.map((ele) => ele.id);
      let { groupName } = this.state;

      if (!groupName) {
        toast.error('Please enter Style Color Group name!', {
          autoClose: 15000,
          hideProgressBar: true,
        });
        return;
      }
      this.props
        .createStyleGroup({
          division: this.props.selectedHierarchy1,
          department: this.props.selectedHierarchy2,
          subdepartment: this.props.selectedHierarchy3,
          program_tag: this.props.selectedHierarchy4,
          lifecycle: this.props.selectedHierarchy5,
          inv_receipt_date: this.props.selectedHierarchy6,
          style_config_name: groupName,
          style_codes: styleCodes,
          style_type: 'group',
        })
        .then((res) => {
          if (res) {
            this.props.history.goBack();
          }
        });
    }
  };

  onCancelButtonHandler = () => {
    // this.setState({ allStores: [], editAllstoreData: [] });
    if (this.props.styleGroupEdit) {
      this.props.styleGroupEditHandler();
      return;
    }
    this.props.history.goBack();
  };

  render() {
    let { groupName, currentStyles, allStyles, header } = this.state;

    return (
      <div className="card p-2">
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <h4 className="font-size-14  pad-10 markdown-heading">
            Style Color Configuration/
            <span className="font-bold color-blue">
              {this.props.styleGroupEdit ? 'Edit Style Color Group' : 'Create Style Color Group'}
            </span>
          </h4>

          <div>
            <button className="btn btn-primary" onClick={this.onCancelButtonHandler}>
              Back
            </button>
          </div>
        </div>
        <hr
          className="mb-2"
          style={{
            marginTop: '-4px',
          }}
        />

        <div className="row justify-content-between">
          <div className="col-md-6">
            <label className="mr-1">
              <span style={{ color: 'red' }}>*</span>Style color group name:
            </label>
            <input
              className="inputText"
              type="text"
              disabled={this.props.styleGroupEdit}
              value={this.state.groupName}
              onChange={this.onGroupNameChange}
            />
          </div>
          <div className="col-md-2 text-right">
            {/* <button type="button" className="btn btn-primary" onClick={() => {}}>
              Upload &nbsp;&nbsp;
              <span>
                <img className="img-icons-create-promo" src={excelIcon} alt="" />
              </span>
            </button> */}
          </div>
        </div>
        <hr />
        <Filters
          hideStoreClusterFilter
          hierarchy={[
            {
              type: 'hierarchy1',
              isMandatory: true,
              isMultiple: false,
            },
            {
              type: 'hierarchy2',
              isMandatory: true,
              isMultiple: true,
            },
            {
              type: 'hierarchy3',
              isMandatory: true,
              isMultiple: true,
            },
            {
              type: 'hierarchy4',
              isMandatory: true,
              isMultiple: true,
            },
            {
              type: 'hierarchy5',
              isMandatory: true,
              isMultiple: true,
            },
            {
              type: 'hierarchy6',
              isMandatory: true,
              isMultiple: true,
            },
          ]}
          hideStoreCluster={true}
          disableDivision={this.props.styleGroupEdit}
          onFilter={this.onFilter}
          from="styleGroupConfig"
        />
        <hr />
        <div className="row mx-2 create-store-grp-table">
          <div className="col-md-6">
            All style Color
            <div className="styleStoreTable">
              <DataTable
                visibleColumns="onlyRowSelection"
                getRowProps={(rowInfo) => ({
                  onClick: () => this.onAllStyleSelectionChangeHandler(rowInfo),
                })}
                columns={header}
                data={allStyles}
                isPagination={true}
              />
            </div>
            <div className="mt-4">
              <button className="btnStyle btn btn-primary" onClick={this.onAddClickHandler}>
                Add
              </button>
            </div>
          </div>
          <div className="col-md-6">
            <div>
              <span className="mr-2">Current</span>
              <span>No of style Color: {currentStyles && currentStyles.length}</span>
            </div>
            <div className="styleStoreTable">
              <DataTable
                visibleColumns="onlyRowSelection"
                getRowProps={(rowInfo) => ({
                  onClick: () => this.onCurrentStylesSelectionChangeHandler(rowInfo),
                })}
                columns={header}
                data={currentStyles}
                isPagination={true}
              />
            </div>
            <div className="mt-4">
              <button className="btnStyle btn btn-primary" onClick={this.onRemoveClickHandler}>
                Remove
              </button>
            </div>
          </div>
        </div>
        <div className="mb-3 mt-4" style={{ textAlign: 'center' }}>
          <button
            style={{ width: '7%', fontSize: '15px' }}
            className="btn btn-primary"
            onClick={this.onCreateStyleGroup}>
            {this.props.styleGroupEdit ? 'Update' : 'Create'}
          </button>
          <button
            style={{ width: '7%', fontSize: '15px' }}
            className="btn ml-1 cancel-button"
            onClick={this.onCancelButtonHandler}>
            Cancel
          </button>
        </div>
      </div>
    );
  }
}

