  //       <div className="abc">
  //         <button className="btn btn-primary mr-3" onClick={this.addScenario}>
  //           Add Scenario
  //         </button>
  //       </div>
  //     </div>
  //   );
  // };
  getCadenceData = (index) => {
    if (this.state.cadenceConstraintsVal === 'overall') return this.state.overallCadenceData[index];
    return this.state.skuLabelCadenceData[index];
  };
  // getTabTag = (tabTitle, index) => {
