  addTitleInTabs = () => {
    //checl comparison is there or not if yes -1
    let TabTitles = this.state.TabTitles;
    let comparisonTitleIndex = TabTitles.indexOf('Comparison');
    let newSceniosIndex = TabTitles.length;
    let defaultSceniosTitle = `Scenario 0${TabTitles.length}`;
    if (comparisonTitleIndex !== -1) {
      //comparsion present
      newSceniosIndex = comparisonTitleIndex;
      defaultSceniosTitle = `Scenario 0${newSceniosIndex}`;
      TabTitles.splice(newSceniosIndex, 0, defaultSceniosTitle);
    } else {
      TabTitles.splice(newSceniosIndex, 0, defaultSceniosTitle);
    }
    this.setState({
      TabTitles,
      defaultActiveKey: defaultSceniosTitle,
    });
    this.addContentsInTabs(newSceniosIndex, defaultSceniosTitle);
  };
  addContentsInTabs = (newSceniosIndex, defaultSceniosTitle) => {
    let data =
      this.state.cadenceConstraintsVal === 'overall'
        ? this.state.overallCadenceData
        : this.state.skuLabelCadenceData;
    // );
    let { simulatedCadenceData } = this.state;
    data.splice(newSceniosIndex, 0, cloneDeep(data[0]));
    simulatedCadenceData.splice(newSceniosIndex, 0, cloneDeep(data[0]));
    let obj = {
      skuLabelCadenceData: data,
    };
    if (this.state.cadenceConstraintsVal === 'overall') {
      obj = {
        overallCadenceData: cloneDeep(simulatedCadenceData), //reset edited input field data
        simulatedCadenceData,
      };
    }
    this.props.updateAnalysisData(
      this.props.analysisData['Optimal'].simulateData,
      defaultSceniosTitle
    );
    this.setState(obj);
  };
  addScenario = () => {
    if (this.isTotalSceniosCreatedFive()) {
      this.setState({
        selectedSkuId: null,
        selectedStyle: 'all',
      });
      this.addTitleInTabs();
      this.setState({ disableSimBtn: true, isDraftDisabled: true });
      return;
    }
    toast.error(`Max 5 Scenarios can be created`, {
      autoClose: 15000,
      hideProgressBar: true,
    });
  };
  isTotalSceniosCreatedFive = () => {
    let totalScenariosCreated = this.state.TabTitles.filter((title) =>
      title.startsWith('Scenario')
    );
    return totalScenariosCreated.length < 5;
  };

  resetCadence = () => {
    this.setState({
      TabTitles: ['Optimal'],
      overallCadenceData: [],
      simulatedData: false,
      showCadence: false,
      defaultActiveKey: 'Optimal',
    });
    this.resetComp();
  };

  validateMargin = (e) => {
    if (
      this.state.minTS < parseInt(this.state.marginVal) &&
      this.state.maxTS > parseInt(this.state.marginVal)
    ) {
      return true;
    }
    let msg = _.isEmpty(this.state.marginVal)
      ? 'Please enter Margin % value'
      : 'Margin % value should be between 0 to 100';
    toast.error(msg, {
      autoClose: 15000,
      hideProgressBar: true,
    });
    return false;
  };
  validateSellThrough = (e) => {
    if (
      this.state.minTS < parseInt(this.state.targetSellThroughVal) &&
      this.state.maxTS > parseInt(this.state.targetSellThroughVal)
    ) {
      return true;
    }
    let msg = _.isEmpty(this.state.targetSellThroughVal)
      ? 'Please enter Target Sell through % value'
      : 'Target Sell through % value should be between 0 to 100';
    toast.error(msg, {
      autoClose: 15000,
      hideProgressBar: true,
    });
    return false;
  };
  checkDateRange = () => {
    let todayDate = moment().format('YYYY-MM-DD'),
      endDate = moment().add(14, 'weeks').format('YYYY-MM-DD'), //user can select 13 weeks from today date
      weeksCheck = moment(this.state.endDate.format('YYYY-MM-DD')).isBetween(todayDate, endDate);

    if (
      this.state.startDate &&
      this.state.endDate &&
      moment().week() + 1 <= this.state.startDate.week() &&
      weeksCheck
    ) {
      return true;
    }
    let msg = _.isEmpty(this.state.startDate)
      ? 'Please enter Start Date'
      : _.isEmpty(this.state.endDate)
      ? 'Please enter End Date'
      : 'Start week should be greater than current week and end date less than 13 weeks';
    toast.error(msg, {
      autoClose: 15000,
      hideProgressBar: true,
    });
    return false;
  };

  generateCadence = () => {
    // if (!this.validateSellThrough() || !this.validateMargin() || !this.checkDateRange()) {
    //   return;
    // }
    this.resetCadence();
    this.setState({
      showCadence: false,
      simulatedData: true,
    });
    let startWeek = this.state.startDate.week();
    let endWeek = this.state.endDate.week();

