    let editMdfilters = {
      filters: dataObj.filters,
    };
    let request =
      this.state.isEdit && !this.state.editBy ? cloneDeep(this.state.filteredData) : editMdfilters;

    console.log(request, 'req');

    let inputData = {
      cadenceData: this.state.cadenceData,
      inventorySelected: '',
      storeSelected: '',
      excludeStore: this.state.excludedStores,
      sellThrough: !this.state.editBy
        ? this.state.targetSellThroughVal || ''
        : dataObj.sellThrough || this.state.targetSellThroughVal,

      margin: this.state.marginVal || '',
      offer_value: this.state.offerType | '',
      markdown_type: this.state.markdownTypeVal || '',
      cadence_type: this.state.cadenceConstraintsVal || 'overall',
      min_md_frequency: this.state.mdFrequency.min,
      max_md_frequency: this.state.mdFrequency.max,
      min_no_of_distinct_md: this.state.noOfDistinctMd.min,
      max_no_of_distinct_md: this.state.noOfDistinctMd.max,
      startDate: this.state.startDate.format('YYYY-MM-DD'),
      endDate: this.state.endDate.format('YYYY-MM-DD'),
      week: weeks,
      totalInDc: !this.state.editBy ? this.state.totalInDc : dataObj.totalInDc,
      totalOnHand: !this.state.editBy ? this.state.totalOnHand : dataObj.totalOnHand,
      totalOnOrder: !this.state.editBy ? this.state.totalOnOrder : dataObj.totalOnHand,
      totalInTransit: !this.state.editBy ? this.state.totalInTransit : dataObj.totalOnHand,
      stores: !this.state.editBy ? this.state.stores : dataObj.stores,
      md_level: this.state.md_level,
      md_level_no_range: weeks,
      startWeek,
      endWeek,
    };
    console.log(inputData.sellThrough, 'sellThrough');
    let saveCadenceRequest = { ...request, ...inputData };
    this.setState({
      saveCadenceRequest,
    });
    this.props.fetchCadence(saveCadenceRequest);
  };
  checkComparsionDataPresent = (data) => {
    return data.indexOf('Comparison') !== -1 ? true : false;
  };
  updateComparsionDataInTab = (tabTitles, firstSimulate) => {
    let comparsionData = [];
    let newComparisonData = [];
    let activeKey = this.state.defaultActiveKey;
    let cadenceData = _.cloneDeep(this.state.overallCadenceData);
    let spliceIndex = firstSimulate ? cadenceData.length : cadenceData.length - 1;
    let tabTitleForComparison = firstSimulate
      ? ['Optimal']
      : _.map(
          cadenceData[cadenceData.length - 1],
          (item, index) => index < cadenceData.length && item.scenarios
        );
    tabTitleForComparison.splice(activeKey.charAt(activeKey.length - 1), 0, activeKey);
    tabTitleForComparison = _.uniq(tabTitleForComparison);
    _.map(tabTitleForComparison, (item) => {
      let findEle = _.find(
        cadenceData,
        (row, idx) => (item === 'Optimal' && idx === 0) || item.charAt(item.length - 1) == idx
      );
      if (findEle) {
        let newObj = {
          scenarios: item,
          ...findEle[0],
        };
        newComparisonData.push(newObj);
      }
    });
    cadenceData.splice(spliceIndex, 1, newComparisonData);

    for (let i = 0; i < tabTitles.length - 1; i++) {
      if (this.state.cadenceConstraintsVal === 'overall') {
        // comparsionData.push({
        //   scenarios: tabTitles[i],
        //   ...cloneDeep(this.state.overallCadenceData[i][0]),
        // });
      } else {
        let rows = cloneDeep(this.state.skuLabelCadenceData[i][0]);
        delete rows['SKU ID'];
        delete rows['MD Status'];
        comparsionData.push({
          scenarios: tabTitles[i],
          ...rows,
        });
      }
    }
    let data = [];
    let minusIndex = 0;
    if (this.checkComparsionDataPresent(this.state.TabTitles)) {
      minusIndex = 1;
    }
    if (this.state.cadenceConstraintsVal === 'overall') {
      // data = cloneDeep(this.state.overallCadenceData);
      // data.splice(data.length - minusIndex, 1, comparsionData);
      this.setState({
        overallCadenceData: cadenceData,
        simulatedCadenceData: _.cloneDeep(cadenceData), //reset edited input field data
        simulatedData: true,
      });
    } else {
      data = cloneDeep(this.state.skuLabelCadenceData);
      data.splice(data.length - minusIndex, 1, comparsionData);
      this.setState({
        skuLabelCadenceData: data,
        simulatedData: true,
      });
    }
  };
  resetComp = () => {
    this.setState({
      comparisonData: {},
    });
  };
  onSimulate = async () => {
    let res = await this.callAPISimulate();
    if (!res.status) return;

    //adding comparsion title
    let tabTitles = cloneDeep(this.state.TabTitles);
    let firstSimulate = false;
    this.setState({
      isDraftDisabled: false,
      selectedSkuId: null,
    });
    if (this.state.TabTitles.indexOf('Comparison') === -1) {
      firstSimulate = true;
      let array = [...this.state.TabTitles, 'Comparison'];
      tabTitles = array;
      this.setState({
        TabTitles: array,
        selectedStyle: 'all',
        selectedSkuId: null,
        // isDraftDisabled: false,
      });
    }
    this.updateComparsionDataInTab(tabTitles, firstSimulate);
    // this.callAPISimulate();
    this.setState({ disableSimBtn: true });
    this.resetComp();
  };
  getStyleColorData = async (skus) => {
    let styleColor = skus.value.split('-');

    let res = await this.props.fetchStyleColorData(
      {
        promo_code: this.state.planId,
        style_id: styleColor[styleColor.length - 2], //1,
        color: styleColor[styleColor.length - 1], //65,
      },
      this.state.defaultActiveKey
    );
    if (
      !_.isEqual(this.state.simulatedCadenceData, this.state.overallCadenceData)
      // &&
      // skus &&
      // skus.value &&
      // skus.value !== this.state.selectedStyle
    ) {
      this.onEditSimulate(skus);
    }
  };
  onEditSimulate = async (skus) => {
    let array = this.getCadenceData(0),
      skusCalc = skus && skus.value ? skus.value : this.state.selectedStyle;
    let dataObj = this.props.editPlanDetails[0] && this.props.editPlanDetails[0].request_obj;
    let scenario = Object.keys(dataObj.analysisData);

    let analysisDataObj = dataObj.analysisData[scenario[0]];
    if (_.isEmpty(dataObj)) return;
    let startWeek = dataObj.startWeek;
    let endWeek = dataObj.endWeek;
    let weeks = this.getWeekRange(startWeek, endWeek);

    let scenario_data_old = {};
    let scenario_data_new = {},
      sell_through_old_overall = {};
    _.map(Object.keys(array[0]), (item, index) => {
      if (item.replace('W', '') <= moment().week()) {
        scenario_data_old[item] = array[0][item];
        sell_through_old_overall[item] =
          dataObj.analysisData[scenario[0]].overview.sellThrough[index];
      }
      if (item.replace('W', '') > moment().week()) {
        scenario_data_new[item] = array[0][item];
      }
    });
    let sell_through_old_sku = [];
    if (!_.isEmpty(Object.keys(scenario_data_old))) {
      _.map(analysisDataObj.detailed.data, (item) => {
        let newObj = {};
        _.map(analysisDataObj.detailed.weeks, (weekNum) => {
          if (weekNum.replace('w', '') <= moment().week()) {
            newObj[weekNum.replace('w', 'W')] = item[`${weekNum}sellThrough`];
          }
        });
        newObj.SKU_Id = item.SKU_Id;
        sell_through_old_sku.push(newObj);
      });
    }
    let inputData = {
      // filters: {
      //   hierarchy1: this.props.selectedHierarchy1,
      //   hierarchy2: this.props.selectedHierarchy2,
      //   hierarchy3: this.props.selectedHierarchy3,
      //   hierarchy4: this.props.selectedHierarchy4,
      //   hierarchy5: this.props.selectedHierarchy5,
      //   hierarchy6: this.props.selectedHierarchy6,
      //   hierarchy7: this.props.selectedHierarchy7,
      //   hierarchy8: this.props.selectedHierarchy8,
      //   tiers: this.props.selectedStores,
      // },
      filters: dataObj.filters,

      inventorySelected: '',
      storeSelected: '',
      sellThrough: dataObj.sellThrough || '',
      margin: dataObj.margin || '',
      markdownType: '',
      cadenceType: this.state.cadenceConstraintsVal || 'overall',
      scenario: 'true',
      cadenceData: this.state.cadenceData,
      startDate: dataObj.startDate,
      endDate: dataObj.endDate,
      week: weeks,

      totalInDc: dataObj.totalInDc,
      totalOnHand: dataObj.totalOnHand,
      totalOnOrder: dataObj.totalOnOrder,
      totalInTransit: dataObj.totalInTransit,
      stores: dataObj.stores,
      md_level: dataObj.md_level,
      md_level_no_range: dataObj.md_level_no_range,
      startWeek,
      endWeek,
      sell_through_old_sku,
      sell_through_old_overall: _.isEmpty(Object.keys(sell_through_old_overall))
        ? []
        : [sell_through_old_overall],
      scenario_data_old: _.isEmpty(Object.keys(scenario_data_old)) ? [] : [scenario_data_old],
      scenario_data_new: [scenario_data_new],
      skus: skusCalc,
      compressedData: {
        saveData: dataObj.simulatedData[scenario[0]],
      },
    };

    let res = await this.props.editSimulate(inputData, this.state.defaultActiveKey);
    if (!res.status) return;
    if (!_.isEqual(this.state.selectedStyle, skusCalc)) {
      this.setState({
        selectedStyle: skusCalc,
        selectedSkuId: skusCalc === 'all' ? null : skus,
      });
    }
    this.setState({ disableSimBtn: true, isDraftDisabled: false });
  };
  callAPISimulate = async (skus = 'all') => {
    let response = [];
    let index = this.state.TabTitles.indexOf(this.state.defaultActiveKey);
    let array = [];

    if (this.state.defaultActiveKey === 'Comparison') {
      let cadence =
        this.state.cadenceConstraintsVal === 'overall'
          ? cloneDeep(this.state.overallCadenceData)
          : cloneDeep(this.state.skuLabelCadenceData);
      let lastComparisonArray = cadence[cadence.length - 1];
      _.map(lastComparisonArray, (item) => {
        let scenios = cloneDeep(item);
        let index =
          item.scenarios === 'Optimal' ? 0 : item.scenarios.charAt(item.scenarios.length - 1);
        delete scenios.scenarios;
        cadence[index] = [scenios];
      });
      let data = cadence[this.state.TabTitles.indexOf('Comparison')];
      for (let i = 1; i < data.length; i++) {
        let row = cloneDeep(data[i]);
        let scenarioName = row.scenarios;
        delete row.scenarios;
        let res = await this.props.simulate(
          {
            csv_names: this.props.csv_names,
            skus: 'all',
            cadenceType: this.state.cadenceConstraintsVal,
            scenario: 'true',
            scenario_data: [row],
          },
          this.state.defaultActiveKey,
          scenarioName
        );
        response = _.isEmpty(response) || response.status ? res : response;
      }
      if (!response.status) return response;

      this.setState({
        overallCadenceData: cadence,
        simulatedCadenceData: _.cloneDeep(cadence), //reset edited input field data
      });
      return response;
    } else {
      // let cadence =
      //   this.state.cadenceConstraintsVal === 'overall'
      //     ? cloneDeep(this.state.overallCadenceData)
      //     : cloneDeep(this.state.skuLabelCadenceData);

      // // let lastComparisonArray = cadence[cadence.length - 1];

      array = this.getCadenceData(index);
      let res = await this.props.simulate(
        {
          csv_names: this.props.csv_names,
          skus,
          cadenceType: this.state.cadenceConstraintsVal,
          // isEdit: this.state.isEdit && skus === 'all' ? true : false,
          scenario: 'true',
          scenario_data: array,
        },
        this.state.defaultActiveKey
      );
      response = res;
    }
    return response;
  };
  changedView = (view) => {
