  };
  excludeStoreValHandler = (selectedRowId = [], selectedRows) => {
    let rowIds = Object.values(selectedRowId);
    let totalStoresData = cloneDeep(this.props.inventoryInfoData.store_data);
    let totalStoreIds = totalStoresData.map((obj) => obj.store_code);
    let totalExcludedStores = rowIds.map((obj) => obj.store_code);
    for (let i = 0; i < totalExcludedStores.length; i++) {
      totalStoreIds.splice(totalStoreIds.indexOf(totalExcludedStores[i]), 1);
    }
    let tempObj = {
      inDc: 0,
      onHand: 0,
      onOrder: 0,
      inTransit: 0,
      stores: 0,
    };
    rowIds.forEach((obj, index) => {
      tempObj.inDc += obj.inventory_data.inventory_in_dc;
      tempObj.onHand += obj.inventory_data.inventory_on_hand;
      tempObj.onOrder += obj.inventory_data.inventory_on_order;
      tempObj.inTransit += obj.inventory_data.inventory_in_transit;
      tempObj.stores = index + 1;
      // return a.inventory_on_hand + b.inventory_on_hand;
    });
    let {
      inventory_in_dc,
      inventory_in_transit,
      inventory_on_hand,
      inventory_on_order,
    } = this.props.inventoryInfoData.totalInv;
    // let DC = inventory_in_dc - tempObj.inDc;
    this.setState({
      totalInDc: inventory_in_dc,
      totalOnHand: inventory_on_hand - tempObj.onHand,
      totalOnOrder: inventory_on_order - tempObj.onOrder,
      totalInTransit: inventory_in_transit - tempObj.inTransit,
      selectedStores: this.props.inventoryInfoData.totalStore - tempObj.stores,
      stores: totalStoreIds,
      excludedRows: selectedRows,
    });
  };
  showTablePopup = () => {
    this.setState({ showExcludeStorePopup: false });
  };
  getWeekRange = (startWeek, endWeek) => {
    let arr = [];
    if (startWeek <= endWeek) {
      for (let i = startWeek; i <= endWeek; i++) {
        arr.push(i);
      }
      return arr;
    }
    if (startWeek > endWeek) {
      let lastWeekNum = moment().weeksInYear();
      let arr = [];
      for (let i = startWeek; i <= lastWeekNum; i++) {
        arr.push(i);
      }
      for (let i = 1; i <= endWeek; i++) {
        arr.push(i);
      }
      return arr;
    }
  };

