          obj.analysisData[temp] = { analysisTabledata, detailed, overview };
        }
        delete sceniosObject.scenarios;
        delete sceniosObject.label;
        delete sceniosObject.value;
        obj.cadenceData[temp] = [sceniosObject];
      });
    } else {
      let key = sceniosNames[0].value || this.state.defaultActiveKey;
      obj.simulatedData[key] = this.props.analysisData[this.state.defaultActiveKey]
        ? this.props.analysisData[this.state.defaultActiveKey].saveData
        : [];
      if (this.state.defaultActiveKey === 'Optimal') {
        obj.analysisData[key] = this.props.editMarkdownAnalysisGraphData;
      } else {
        const { analysisTabledata, detailed, overview } = this.props.analysisData[
          this.state.defaultActiveKey
        ];
        obj.analysisData[key] = { analysisTabledata, detailed, overview };
        console.log(obj.analysisData[key], 'obj.analysisData[key]');
      }
      let data =
        this.state.cadenceConstraintsVal === 'overall'
          ? this.state.overallCadenceData[index]
          : this.state.skuLabelCadenceData[index];
      obj.cadenceData[key] = data;
    }
    this.setState({
      showPlanSavePopup: false,
    });
    this.props.saveData(obj);
  };
