        dispatch({
          type: index === 9 ? 'SET_SELECTED_STORES' : `SET_SELECTED_HIERARCHY${index + 1}`,
          payload: item,
        });
        dispatch({
          type: index === 9 ? 'SET_STORES' : `SET_HIERARCHY${index + 1}`,
          payload: _.map(item, (val) => ({ value: val, label: val })),
        });
        dispatch({
          type: index === 8 && 'SET_SELECTED_CUSTOM_GROUPS',
          payload: item,
        });
        dispatch({
          type: index === 8 && 'SET_STYLE_GROUPS',
          payload: _.map(item, (val) => ({ value: val, label: val })),
        });
