import React, { useReducer } from 'react';
import Select from 'react-select';
import Filters from '../../../services/filters';
import DataTable from '../../../services/reactTable';
import '../../Configuration/StoreConfigContainers/StoreConfig.css';
import excelIcon from '../../../images/leftPanel/status.svg';
import { connect } from 'react-redux';

const initialState = {
  styleId: '',
  styleName: '',
  department: '',
  edlpValue: '',
  subDepartment: '',
  class: '',
  subClass: '',
  refStyle: '',
  replacementStyle: '',
  mapping: 'reference',
};

function reducer(state, action) {
  switch (action.type) {
    case 'styleIdChange':
      return { ...state, styleId: action.value };
    case 'styleNameChange':
      return { ...state, styleName: action.value };
    case 'departmentChange':
      return { ...state, department: action.value };
    case 'edplChange':
      return { ...state, edlpValue: action.value };
    case 'subDepartmentChange':
      return { ...state, subDepartment: action.value };
    case 'classChange':
      return { ...state, class: action.value };
    case 'subClassChange':
      return { ...state, subClass: action.value };
    case 'refStyleChange':
      return { ...state, refStyle: action.value };
    case 'replacementStyleChange':
      return { ...state, replacementStyle: action.value };
    case 'mapping':
      return { ...state, mapping: action.value };
    default:
      throw new Error();
  }
}

const CreateStyle = (props) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <div className="card p-2">
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <h4 className="font-size-14  pad-10 markdown-heading">
          Store Configuration/
          <span className="font-bold color-blue">{props.isEditStyle ? 'Edit' : 'Map'} Style</span>
        </h4>
        <div>
          <button className="btn btn-primary" onClick={props.onCancelButtonHandler}>
            Back
          </button>
        </div>
      </div>
      <hr
        className="mb-2"
        style={{
          marginTop: '-4px',
        }}
      />
      <div>
        <Filters
          hideStoreClusterFilter
          hidefilterButton
          hierarchy={['hierarchy1', 'hierarchy2', 'hierarchy3', 'hierarchy4']}
          // onFilter={this.onFilter}
        />
        <div className="row">
          <div className="col-md-2 mt-2" style={{ marginLeft: '39px' }}>
            <label className="mb-1 mt-3">Search by Style Id</label>
            <Select
              id="store-group-select"
              placeholder="Style Id"
              // options={this.props.storeGroups}
              // value={this.state.storeGroup}
              // onChange={this.onStoreGroupChangeHandler}
            />
          </div>
          <div className="col-md-2 mt-5">
            <button
              // onClick={() => this.onFilter}
              className="col-md-5 btn btn-primary btn-filter">
              <span>
                <i className="fa fa-filter"></i>&nbsp; Filter
              </span>
            </button>
            <button
              className="col-md-6 offset-1 btn btn-primary btn-filter btn-filter-reset"
              // onClick={this.reset}
            >
              <span>
                <i className="fa fa-repeat"></i>&nbsp; Reset
              </span>
            </button>
          </div>
        </div>

        {state.department && state.department.value === 'GOLF BALLS' && (
          <div className="row p-2" style={{ justifyContent: 'center' }}>
            <div className="col-md-4">
              <>
                <label className="input-label">
                  {state.department.value === 'GOLF BALLS' && 'EDLP Flag'}
                </label>
                {state.department.value === 'GOLF BALLS' && (
                  <Select
                    // isDisabled={this.props.loading}
                    name="edlp"
                    placeholder="Select edlp"
                    // allowSelectAll
                    options={edlpData}
                    value={state.edlpValue}
                    onChange={(val) => dispatch({ type: 'edplChange', value: val })}
                  />
                )}
              </>
            </div>
            <div className="col-md-3"></div>
          </div>
        )}
      </div>
      <hr />
      <div>
        <div>Style mapping</div>
        <div className="row p-4">
          <input
            className="align-self-center"
            id="reference"
            type="radio"
            name="mapping"
            value="reference"
            checked={state.mapping === 'reference'}
            onChange={(e) => dispatch({ type: 'mapping', value: e.target.value })}
          />
          &nbsp;&nbsp;
          <label className="align-self-center" style={{ margin: '0px' }} htmlFor="reference">
            Refence style
          </label>
          <div className="col-md-2 align-self-center">
            <Select
              name="refStyle"
              placeholder="Select Refence Style"
              options={styleData}
              value={state.refStyle}
              onChange={(val) => dispatch({ type: 'refStyleChange', value: val })}
            />
          </div>
        </div>
        <div className="row p-4">
          <input
            className="align-self-center"
            id="repStyle"
            type="radio"
            name="mapping"
            value="replacement"
            checked={state.mapping === 'replacement'}
            onChange={(e) => dispatch({ type: 'mapping', value: e.target.value })}
          />
          &nbsp;&nbsp;
          <label className="align-self-center" style={{ margin: '0px' }} htmlFor="repStyle">
            Replacementstyle
          </label>
          <div className="col-md-4 align-self-center">
            <Select
              name="repStyle"
              placeholder="Select Replacement Style"
              options={styleData}
              value={state.replacementStyle}
              onChange={(val) => dispatch({ type: 'replacementStyleChange', value: val })}
            />
          </div>
          <div className="col-md-4">*Changes will be reflected within 24 hrs</div>
        </div>
      </div>
      <hr />

      <div className="mb-3 mt-4" style={{ textAlign: 'center' }}>
        <button
          style={{ width: '7%', fontSize: '15px' }}
          className="btn btn-primary"
          onClick={() => props.onCreateStyleHandler(state)}>
          Create
        </button>
        <button
          style={{ width: '7%', fontSize: '15px' }}
          className="btn ml-1 cancel-button"
          onClick={props.onCancelButtonHandler}>
          Cancel
        </button>
      </div>
    </div>
  );
};

export default CreateStyle;
