import React, { Component } from 'react';
import styled from 'styled-components';
import moment from 'moment';
import './index.css';
import _ from 'lodash';
import { toast } from 'react-toastify';

class CreateCadence extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      selectedData: [],
    };
  }
  // componentDidMount() {
  //   this.setState({
  //     data: this.props.overallCadenceData,
  //   });
  // }
  getHeader() {
    if (this.props.overallCadenceData && this.props.overallCadenceData.length) {
      let headers = [];
      if (this.props.cadenceConstraintsVal === 'overall') {
        //overall case
        headers = _.filter(Object.keys(this.props.overallCadenceData[0]), (o) => o !== 'bins');
        headers.unshift('bins'); //add bins col at first
      }
      if (this.props.cadenceConstraintsVal === 'sku') {
        //sku case
        headers = _.filter(Object.keys(this.props.overallCadenceData[0]), (o) => o !== 'SKU_Id');
        headers.unshift('SKU ID'); //add sku_id col at first
      }

      return headers.map((header, index) => {
        return <th key={index}>{header[0].toUpperCase() + header.slice(1)}</th>;
      });
    }
  }
  onCadenceValue(event, col, row, rowNumber) {
    let data = this.props.overallCadenceData;
    // row = { ...row };
    if (
      this.props.offerType == 'pp'
        ? !(event.target.value >= this.props.discountPP.min) ||
          !(event.target.value <= this.props.discountPP.max) ||
          !(event.target.value % this.props.discountPP.interval === 0)
        : !(event.target.value >= this.props.discountOff.min) ||
          !(event.target.value <= this.props.discountOff.max) ||
          !(event.target.value % this.props.discountOff.interval === 0)
    ) {
      this.props.offerType == 'pp'
        ? toast.error(
            `Please enter values between ${this.props.discountPP.min} and ${this.props.discountPP.max} in intervals of ${this.props.discountPP.interval}`,
            {
              autoClose: 15000,
              hideProgressBar: true,
            }
          )
        : toast.error(
            `Please enter values between ${this.props.discountOff.min} and ${this.props.discountOff.max} in intervals of ${this.props.discountOff.interval}`,
            {
              autoClose: 15000,
              hideProgressBar: true,
            }
          );
      return;
    } else {
      // this.setState({
      //   data: data,
      // });
      // debugger;
      row[col] = event.target.value;
      data[rowNumber] = row;
      this.props.onUpdateMarkdownPercentage(data, this.props.index, event.target.value);
    }
  }
  getInputWithPercentageTag(col, row, rowNumber, i) {
    let disableInput =
      this.props.weekInfo &&
      this.props.weekInfo.pastWeeks &&
      this.props.weekInfo.pastWeeks.includes(col)
        ? true
        : false;
    return (
      <div className={`col ${this.props.offerType ? 'ppWidth' : 'percenWidth'}`} key={i + 1}>
        <div className="input-group">
          {this.props.offerType == 'pp' ? (
            <div class="input-group-prepend">
              <span class="input-group-text">$</span>
            </div>
          ) : null}

          <input
            type="number"
            step="5"
            className="form-control border-style"
            onChange={(e) => this.onCadenceValue(e, col, row, rowNumber, i)}
            value={row[col]}
            min={
              this.props.offerType == 'pp' ? this.props.discountPP.min : this.props.discountOff.min
            }
            max={
              this.props.offerType == 'pp' ? this.props.discountPP.max : this.props.discountOff.max
            }
            step={
              this.props.offerType == 'pp'
                ? this.props.discountPP.interval
                : this.props.discountOff.interval
            }
            disabled={
              this.props.isEdit
                ? this.props.activeTab === 'Optimal' || disableInput
                : this.props.activeTab === 'Optimal'
            }
          />
          <div className="input-group-append">
            {this.props.offerType == 'pp' ? (
              <button
                className="btn btn-side-percent"
                type="button"
                style={{ zIndex: 'unset', fontSize: '15px' }}>
                PP
              </button>
            ) : (
              <button className="btn btn-side-percent" type="button" style={{ zIndex: 'unset' }}>
                <i className="fa fa-percent" />
              </button>
            )}
          </div>
        </div>
      </div>
    );
  }

  getTags(col, row, rowNumber, i) {
    if (col.toLowerCase().startsWith('w')) {
      return this.getInputWithPercentageTag(col, row, rowNumber, i);
    } else if (col.toLowerCase().startsWith('m')) {
      return (
        <div
          style={{
            backgroundColor: row[col] === 'Medium' ? '#D6B93E' : '#E59393',
            color: 'white',
            borderRadius: '6px',
          }}>
          {row[col]}
        </div>
      );
    } else {
      return row[col];
    }
  }

  render() {
    return (
      <div
        className={
          this.props.cadenceConstraintsVal == 'overall'
            ? 'createCadenceTable'
            : this.props.overallCadenceData && this.props.overallCadenceData.length >= 10
            ? 'createCadenceTableComparison'
            : 'createCadenceTable'
        }>
        <Styles>
          <tbody>
            <tr>{this.getHeader()}</tr>
            {this.props.overallCadenceData &&
              this.props.overallCadenceData.length &&
              this.props.overallCadenceData.map((row, rowNumber) => {
                let rows = [];
                if (this.props.cadenceConstraintsVal === 'overall') {
                  //overall case
                  rows = _.filter(Object.keys(row), (o) => o !== 'bins');
                  rows.unshift('bins'); //add bins col at first
                }
                if (this.props.cadenceConstraintsVal === 'sku') {
                  //sku case
                  rows = _.filter(Object.keys(row), (o) => o !== 'SKU_Id');
                  rows.unshift('SKU_Id'); //add sku_id col at first
                }
                return (
                  <tr>
                    {rows.map((col, i) => {
                      return (
                        <td style={{ width: '1%' }}>{this.getTags(col, row, rowNumber, i)}</td>
                      );
                    })}
                  </tr>
                );
              })}
          </tbody>
        </Styles>
      </div>
    );
  }
}

export default CreateCadence;

const Styles = styled.table`
  font-family: arial, sans-serif;
  border-collapse: collapse;
  width: 100%;

  td,
  th {
    border: 1px solid #dddddd;
    text-align: center;
    padding: 8px;
  }
`;
