import _ from 'lodash';

const initialState = {
  markdownAnalysisGraphData: [],
  editMarkdownAnalysisGraphData: [],
  analysisTable: [],
  editAnalysisData: [],
  comparisonData: {},
  analysisData: {},
  EditanalysisData: {},
  analysisTableBySkus: {},
  analysisDataBySkus: {},
  editAnalysisTableDataBySkus: {},
  markdownAnalysisGraphDataBySkus: {},
  editMarkdownAnalysisGraphDataBySkus: [],
};

// const getComparsionTableData = (payload) => {

// }

export default function (state = initialState, action) {
  switch (action.type) {
    case 'RESET_INVENTORY_INFO_DATA':
      return {
        ...state,
        markdownAnalysisGraphData: [],
        editMarkdownAnalysisGraphData: [],
        analysisTable: [],
        comparisonData: {},
        analysisData: {},
        analysisTableBySkus: {},
        analysisDataBySkus: {},
        markdownAnalysisGraphDataBySkus: {},
      };
    case 'GET_MARKDOWNANALYSIS_GRAPH_DATA': {
      if (action.isEdit) {
        _.map(state.markdownAnalysisGraphData.detailed.data, (item) => {
          item.metric = 'Previous';
          return item;
        });
        _.map(action.payload.detailed.data, (item) => {
          item.metric = 'Edited';
          return item;
        });
        return {
          ...state,
          editMarkdownAnalysisGraphData: action.payload,
          markdownAnalysisGraphData: state.markdownAnalysisGraphData,
        };
      }
      return {
        ...state,
        markdownAnalysisGraphData: action.payload,
      };
    }
    case 'GET_MARKDOWNANALYSIS_GRAPH_DATA_BY_SKUS': {
      if (action.isEdit) {
        _.map(state.markdownAnalysisGraphDataBySkus.detailed.data, (item) => {
          item.metric = 'Previous';
          return item;
        });
        _.map(action.payload.detailed.data, (item) => {
          item.metric = 'Edited';
          return item;
        });
        return {
          ...state,
          editMarkdownAnalysisGraphDataBySkus: action.payload,
          markdownAnalysisGraphDataBySkus: state.markdownAnalysisGraphDataBySkus,
        };
      }
      return {
        ...state,
        markdownAnalysisGraphDataBySkus: action.payload,
      };
    }

    case 'SET_ANALYSIS_TABLE_DATA_BY_SKUS': {
      let obj = {};
      action.payload.analysisTabledata.forEach((ob) => {
        obj[ob.style_id] = ob.style_desc;
      });
      obj.scenario = action.cadenceType;
      let data = [];
      // if (action.comparisonData && action.comparisonData.length === 0) {
      //   data = [];
      // } else {
      //   data = state.comparisonData;
      // }
      // let data = action.cadenceType === 'Optimal' ? [] : state.comparisonData;
      let analysisData = state.analysisDataBySkus;
      if (action.cadenceType !== 'Comparison') {
        data[action.cadenceType] = obj;
        analysisData[action.cadenceType] = action.data;
      }
      // else {
      //   data[action.cadenceType] = obj;
      //   analysisData[action.cadenceType] = action.data;
      // }
      if (action.isEdit) {
        return {
          ...state,
          editAnalysisTableDataBySkus: action.payload,
          analysisDataBySkus: analysisData,
        };
      }
      return {
        ...state,
        analysisTableBySkus: action.payload,
        // comparisonData: data,
        analysisDataBySkus: analysisData,
      };
    }
    case 'SET_ANALYSIS_TABLE_DATA':
      let obj = {};
      action.payload.analysisTabledata.forEach((ob) => {
        obj[ob.style_id] = ob.style_desc;
      });
      obj.scenario = action.cadenceType;
      let data = {}; //array of key value pairs is not a proper way to store data,  so changing it to object of keyvalue pairs
      if (action.comparisonData && action.comparisonData.length === 0) {
        data = {}; //array of key value pairs is not a proper way to store data,  so changing it to object of keyvalue pairs
      } else {
        data = state.comparisonData;
      }
      // let data = action.cadenceType === 'Optimal' ? [] : state.comparisonData;
      let analysisData = state.analysisData;
      if (action.cadenceType !== 'Comparison') {
        data[action.cadenceType] = obj; //array of key value pairs is not a proper way to store data,  so changing it to object of keyvalue pairs
        analysisData[action.cadenceType] = action.data;
      }
      // else {
      //   data[action.cadenceType] = obj;
      //   analysisData[action.cadenceType] = action.data;
      // }
      if (action.isEdit) {
        console.log(EditanalysisData, 'EditanalysisData');
        return {
          ...state,
          editAnalysisData: action.payload,
          EditanalysisData: analysisData,
        };
      }
      return {
        ...state,
        analysisTable: action.payload,
        comparisonData: data,
        analysisData: analysisData,
      };
    default:
      return {
        ...state,
      };
  }
}
