import axios from '../utils/axios';
import { config } from '../utils/config';
import { toast } from 'react-toastify';

const summaryAction = {
  onResetSummaryTable: () => {
    return (dispatch) => {
      dispatch({
        type: 'RESET_SUMMARY',
      });
    };
  },
  getSummaryDetails: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.createMarkdownURL}/summaryDetail/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          const { detailed_data } = resp.data.data;
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          dispatch({
            type: 'SET_DETAILED_SUMMARY',
            payload: detailed_data,
          });
          return resp.data.data;
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },

  getSummaryAggregated: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.createMarkdownURL}/summaryAgg/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          dispatch({
            type: 'SET_AGGREGATED_SUMMARY',
            payload: resp.data.data,
            pageNum: reqObj.page_num,
          });
          dispatch({
            type: 'SET_MARKDOWN_DETAILS',
            payload: resp.data.data.markdown_details,
          });
          return resp.data.data;
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
  getKPIdata: (payload) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.createMarkdownURL}/summaryKPI/`,
        method: 'POST',
        data: payload,
      })
        .then((resp) => {
          const { data } = resp.data;
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          dispatch({
            type: 'SET_KPI_DATA',
            value: data,
          });
          return data;
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
  sendResimulateAndSave: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.createMarkdownURL}/reSimulateAndSave/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          console.log(resp.data.data, 'resppppp');
          let data = resp.data.data;
          _.map(Object.keys(data), (promo_code) => {
            let msg = data[promo_code];
            if (msg.status) {
              toast.success(`${msg.message}`, {
                autoClose: 15000,
                hideProgressBar: true,
              });
            } else {
              toast.error(`${msg.message[0]} for promo code :  ${promo_code}`, {
                autoClose: 15000,
                hideProgressBar: true,
              });
            }
          });

          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return resp;
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return err;
        });
    };
  },
};

export default summaryAction;
