import React, { Component } from 'react';
import styled from 'styled-components';
import moment from 'moment';
import './index.css';
class CreateCadence extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      selectedData: [],
    };
  }
  // componentDidMount() {
  //   this.setState({
  //     data: this.props.overallCadenceData,
  //   });
  // }
  getHeader() {
    if (this.props.overallCadenceData && this.props.overallCadenceData.length) {
      let headers = Object.keys(this.props.overallCadenceData[0]);
      return headers.map((header, index) => {
        return <th key={index}>{header[0].toUpperCase() + header.slice(1)}</th>;
      });
    }
  }
  onCadenceValue(event, col, row, rowNumber) {
    let data = this.props.overallCadenceData;
    // row = { ...row };
    row[col] = event.target.value;
    data[rowNumber] = row;
    // this.setState({
    //   data: data,
    // });
    // debugger;
    this.props.onUpdateMarkdownPercentage(data, this.props.index);
  }
  getInputWithPercentageTag(col, row, rowNumber, i, index) {
    let currentWeek = this.props.editPlanDetails[0] && this.props.editPlanDetails[0].current_week;
    console.log(currentWeek, 'currentWeek');
    let disableInput = col.slice(1, 3) <= currentWeek ? true : false;

    return (
      <div className={`col ${this.props.offerType ? 'ppWidth' : 'percenWidth'}`} key={i + 1}>
        <div className="input-group">
          {this.props.offerType.value == 'pp' ? (
            <div class="input-group-prepend">
              <span class="input-group-text">$</span>
            </div>
          ) : null}

          <input
            type="number"
            step="5"
            className="form-control border-style"
            onChange={(e) => this.onCadenceValue(e, col, row, rowNumber, index, i)}
            value={row[col]}
            disabled={this.props.activeTab === 'Optimal'}
          />
          <div className="input-group-append">
            {this.props.offerType.value == 'pp' ? (
              <button className="btn btn-side-percent" type="button" style={{ zIndex: 'unset' }}>
                PP
              </button>
            ) : (
              <button className="btn btn-side-percent" type="button" style={{ zIndex: 'unset' }}>
                <i className="fa fa-percent" />
              </button>
            )}
          </div>
        </div>
      </div>
    );
  }

  getTags(col, row, rowNumber) {
    if (col.toLowerCase().startsWith('w')) {
      return this.getInputWithPercentageTag(col, row, rowNumber);
    } else if (col.toLowerCase().startsWith('m')) {
      return (
        <div
          style={{
            backgroundColor: row[col] === 'Medium' ? '#D6B93E' : '#E59393',
            color: 'white',
            borderRadius: '6px',
          }}>
          {row[col]}
        </div>
      );
    } else {
      return row[col];
    }
  }

  render() {
    return (
      <div className="createCadenceTable">
        <Styles>
          <tbody>
            <tr>{this.getHeader()}</tr>
            {this.props.overallCadenceData &&
              this.props.overallCadenceData.length &&
              this.props.overallCadenceData.map((row, rowNumber) => {
                let rows = Object.keys(row);
                return (
                  <tr>
                    {rows.map((col) => {
                      return <td style={{ width: '1%' }}>{this.getTags(col, row, rowNumber)}</td>;
                    })}
                  </tr>
                );
              })}
          </tbody>
        </Styles>
      </div>
    );
  }
}

export default CreateCadence;

const Styles = styled.table`
  font-family: arial, sans-serif;
  border-collapse: collapse;
  width: 100%;

  td,
  th {
    border: 1px solid #dddddd;
    text-align: center;
    padding: 8px;
  }
`;
