import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import Tabs from 'react-bootstrap/Tabs';
import Tab from 'react-bootstrap/Tab';
import LoadingOverlay from '../../utils/Loader';
import filterActions from '../../actions/filtersAction';
import draftStausAction from '../../actions/draftStausFinalizeAction';
import styled from 'styled-components';
import Draft from './draft';
import Status from './status';
import './draftStatus.css';
import { connect } from 'react-redux';
import _ from 'lodash';
import { toPercentage, toDollarWithlDraft, toUnitDraft } from '../../helper/formatter';
import draftColdef from './draft/coldef';
import IndeterminateCheckbox from '../../components/cellRenders/inderterminateCheckbox';

function FormatterStyling(props, value, val) {
  let val1 = val || val == 0 ? val : props.value;
  let props1 = val || val == 0 ? { value: val } : props;
  if (
    value == 'pvh_units' ||
    value == 'markdown_units' ||
    value == 'overall_pvh_units' ||
    value == 'overall_markdown_units'
  ) {
    return val1 || val1 == 0 ? toUnitDraft(props1) : '-';
  } else if (value == 'margin_perc' || value == 'overall_margin_perc') {
    return val1 || val1 == 0 ? toPercentage(props1) : '-';
  } else if (
    value == 'revenue$' ||
    value == 'aur' ||
    value == 'margin$' ||
    value == 'aum' ||
    value == 'overall_aum' ||
    value == 'overall_revenue$' ||
    value == 'overall_aur' ||
    value == 'overall_margin$'
  ) {
    return val1 || val1 == 0 ? toDollarWithlDraft(props1) : '-';
  }
  return '-';
}

class DraftStatus extends Component {
  constructor(props) {
    super(props);
    this.state = {
      toggle: false,
      currentTab:
        props.location.state && props.location.state.tab ? props.location.state.tab : 'draft',
      draftSelectedItems: [],
      draftColumnOptions: [],
      draftSelLabels: [],
      statusSelectedItems: [],
      statusColumnOptions: [],
      statusSelLabels: [],
      draftColumns: [],
      statusColumns: [],
      statusCounts: {},
      scenarioSelected: { value: false, row: [] },
    };
  }

  // componentDidUpdate(props, state) {
  //   if (state.currentTab !== this.state.currentTab) {
  //     this.props.setMarkDowns([]);
  //     this.props.setMDColumnHeaders({});
  //   }
  // }
  componentWillUnmount() {
    this.props.setMarkDowns([]);
    this.props.setMDColumnHeaders({});
  }
  scenarioSelectedHandler = (value, row) => {
    this.setState({ scenarioSelected: { value, row } });
  };
  onToggleTab = (tab) => {
    this.setState({
      toggle: !this.state.toggle,
      currentTab: tab,
    });
    this.props.setRetainFilters(true);
  };

  onHandleSelect = (selOptions, proceed) => {
    let {
      currentTab,
      draftSelectedItems,
      statusSelectedItems,
      draftColumns,
      statusColumns,
    } = this.state;
    let selectedItems = currentTab === 'draft' ? draftSelectedItems : statusSelectedItems;
    let columns = currentTab === 'draft' ? draftColumns : statusColumns;
    if (!_.isEqual(selectedItems, selOptions) || proceed) {
      let headers = _.cloneDeep(columns);
      _.remove(headers, (item) => item.metric);

      selOptions.forEach((item) => {
        let newTableCols = headers.filter((a) => {
          return a.accessor === item.value;
        });
        if (newTableCols.length == 0) {
          let spliceAt = headers.length - 1;
          headers.splice(spliceAt, 0, {
            Header: item.label,
            accessor: item.value,
            // Cell: (props) => {
            // },
            Cell: (props) => FormatterStyling(props, item.value),
            Aggregated: (props) => {
              let accessorName = `overall_${item.value}`;
              let val = props.row.original[accessorName];
              return FormatterStyling(props, accessorName, val);

              // return <span>{val ? Math.round(val) : '-'}</span>;
            },
            metric: true,
          });
        }
      });
      this.setState((state) => {
        if (state.currentTab === 'draft') {
          return {
            draftColumns: headers,
            draftSelectedItems: selOptions,
          };
        }
        return {
          statusColumns: headers,
          statusSelectedItems: selOptions,
        };
      });
    }
  };

  onHandleCheckBox = (e, label, index) => {
    let {
      currentTab,
      draftSelectedItems,
      statusSelectedItems,
      draftSelLabels,
      statusSelLabels,
    } = this.state;
    let selectedItems = currentTab === 'draft' ? draftSelectedItems : statusSelectedItems;
    let selLabels = currentTab === 'draft' ? draftSelLabels : statusSelLabels;
    let getColumnHeaders =
      currentTab === 'draft' ? this.props.draftColumnHeaders : this.props.statusColumnHeaders;

    //adding
    if (e.target.checked) {
      if (selLabels.indexOf(label) === -1) {
        let labels = [...selLabels, label];
        let tempArr = [];
        labels.forEach((label) => {
          let columns = getColumnHeaders[label];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((label) => {
            return {
              label: label,
              value: columns[label],
            };
          });
          tempArr.push(...options);
        });

        this.setState((state) => {
          if (state.currentTab === 'draft') {
            return {
              draftSelLabels: labels,
              draftColumnOptions: tempArr,
            };
          }
          return {
            statusSelLabels: labels,
            statusColumnOptions: tempArr,
          };
        });
      }
    } else {
      //removing
      let index = selLabels.indexOf(label);
      if (index !== -1) {
        let selectedLabel = _.cloneDeep(selLabels);
        selectedLabel.splice(index, 1);
        let tempArr = [];
        selectedLabel.forEach((label) => {
          let columns = getColumnHeaders[label];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((label) => {
            return {
              label: label,
              value: columns[label],
            };
          });
          tempArr.push(...options);
        });
        let selectedItemsClone = _.cloneDeep(selectedItems);
        let newSelectedItems = [];
        let labelArr = Object.keys(getColumnHeaders[label]);
        selectedItemsClone.forEach((item) => {
          if (!labelArr.includes(item.label)) {
            newSelectedItems.push(item);
          }
        });

        this.onHandleSelect(newSelectedItems);
        this.setState((state) => {
          if (state.currentTab === 'draft') {
            return {
              draftSelLabels: selectedLabel,
              draftColumnOptions: tempArr,
              draftSelectedItems: newSelectedItems,
            };
          }
          return {
            statusSelLabels: selectedLabel,
            statusColumnOptions: tempArr,
            statusSelectedItems: newSelectedItems,
          };
        });
        // this.setState({
        //   selLabels: selectedLabel,
        //   columnOptions: tempArr,
        //   selectedItems: newSelectedItems,
        // });
      }
    }
  };

  getMarkdowns = (payload) => {
    this.props.getMarkdowns(payload).then((resp) => {
      let tempStatusObj = {};
      let markdowns =
        this.state.currentTab === 'draft' ? this.props.draftMarkdowns : this.props.statusMarkdowns;
      markdowns.forEach((row) => {
        if (tempStatusObj.hasOwnProperty(row.status)) {
          tempStatusObj[row.status] += 1;
        } else {
          tempStatusObj[`${row.status}`] = 1;
        }
      });
      this.setState({
        statusCounts: tempStatusObj,
      });
      if (resp && resp.columnHeaders && Object.keys(resp.columnHeaders).length) {
        let defaultHeader = Object.keys(resp.columnHeaders.default);

        let setDefaultColDef = draftColdef().filter(
          (col) =>
            defaultHeader.includes(col.Header) ||
            col.Header === 'Time Period' ||
            // (this.state.toggle && col.Header === 'Actions') ||
            col.id === 'expander' ||
            (this.state.currentTab === 'status' && col.Header === 'Status')
        );
        let setDefaultLabel = [];
        let setDefaultColumnOptions = [];
        let setDefaultColumnOptions1 = [];
        let setDefaultItems = [];
        let colHeaders = _.cloneDeep(resp.columnHeaders);
        let labels = Object.keys(colHeaders);
        labels.splice(labels.indexOf('fixed'), 1);
        labels.splice(labels.indexOf('default'), 1);
        for (const label of labels) {
          let labelArray = Object.keys(resp.columnHeaders[label]);
          function diff(A = labelArray, B = defaultHeader) {
            return A.filter(function (a) {
              return B.indexOf(a) !== -1;
            });
          }

          if (diff().length) {
            setDefaultColumnOptions.push(...Object.keys(resp.columnHeaders[label]));
            setDefaultColumnOptions1.push(resp.columnHeaders[label]);
            setDefaultItems.push(...diff());
            setDefaultLabel.push(label);
          }
        }
        let options = setDefaultColumnOptions.map((label) => {
          let find = setDefaultColumnOptions1 && setDefaultColumnOptions1[0][label];
          return {
            label: label,
            value: find,
          };
        });
        let selectedItems = setDefaultItems.map((label) => {
          let find = setDefaultColumnOptions1 && setDefaultColumnOptions1[0][label];
          return {
            label: label,
            value: find,
          };
        });
        if (this.state.currentTab === 'draft') {
          setDefaultColDef.push({
            id: 'selection',
            groupByBoundary: true,
            Header: 'Actions',
            // fixed: 'left',
            Cell: (props) => {
              let { row, rows } = props;
              let mdPlan = row.original.markdown_id,
                mainGrp = _.find(rows, {
                  isSelected: true,
                  original: { markdown_id: mdPlan },
                }),
                checkboxDisabled = mainGrp && !row.isSelected ? true : false;

              return row.canExpand ? (
                <div>
                  <IndeterminateCheckbox
                    row={row.id}
                    onClick={() => {
                      this.scenarioSelectedHandler(true, row);
                    }}
                    disabled={checkboxDisabled}
                    {...row.getToggleRowSelectedProps()}
                  />
                </div>
              ) : null;
            },
          });
        }
        this.setState((state) => {
          if (state.currentTab === 'draft') {
            return {
              draftColumns: setDefaultColDef,
              draftSelLabels: setDefaultLabel,
              draftColumnOptions: options,
              draftSelectedItems: selectedItems,
            };
          }
          return {
            statusColumns: setDefaultColDef,
            statusSelLabels: setDefaultLabel,
            statusColumnOptions: options,
            statusSelectedItems: selectedItems,
          };
        });
        // this.setState({
        //   columns: setDefaultColDef,
        //   selLabels: setDefaultLabel,
        //   selectedItems: selectedItems,
        //   columnOptions: options,
        // });
        if (selectedItems) {
          let filteredHeader = _.find(options, { accessor: selectedItems[0].value });
          if (!filteredHeader) {
            this.onHandleSelect(selectedItems, true);
          }
        }
      }
    });
  };
  render() {
    return (
      <Styles>
        <LoadingOverlay loader={this.props.getOverlayLoaderState} spinner>
          <Tabs
            defaultActiveKey={this.state.currentTab}
            id="uncontrolled-tab-example"
            onSelect={this.onToggleTab}>
            <Tab eventKey="draft" title="Draft">
              <Draft
                getMarkdowns={this.getMarkdowns}
                columns={this.state.draftColumns}
                scenarioSelectedHandler={this.scenarioSelectedHandler}
                scenarioSelected={this.state.scenarioSelected}
                onHandleSelect={this.onHandleSelect}
                selectedItems={this.state.draftSelectedItems}
                onHandleCheckBox={this.onHandleCheckBox}
                columnOptions={this.state.draftColumnOptions}
                selLabels={this.state.draftSelLabels}
              />
            </Tab>
            <Tab eventKey="status" title="Status">
              <Status
                getMarkdowns={this.getMarkdowns}
                currentTab={this.state.currentTab}
                columns={this.state.statusColumns}
                statusCounts={this.state.statusCounts}
                onHandleSelect={this.onHandleSelect}
                selectedItems={this.state.statusSelectedItems}
                onHandleCheckBox={this.onHandleCheckBox}
                columnOptions={this.state.statusColumnOptions}
                selLabels={this.state.statusSelLabels}
              />
            </Tab>
          </Tabs>
        </LoadingOverlay>
      </Styles>
    );
  }
}
