import React, { Component } from 'react';
import Tabs from 'react-bootstrap/Tabs';
import Tab from 'react-bootstrap/Tab';
import LoadingOverlay from '../../utils/Loader';
import draftStausAction from '../../actions/draftStausFinalizeAction';
import styled from 'styled-components';
import Draft from './draft';
import Status from './status';
import './draftStatus.css';
import { connect } from 'react-redux';
import _ from 'lodash';
import {
  toPercentage,
  toPercentageWithDecimalDraft,
  toDollarWithlDraft,
  toUnitDraft,
  toFixedToTwoDraft,
} from '../../helper/formatter';
import draftColdef from './draft/coldef';
import IndeterminateCheckbox from '../../components/cellRenders/inderterminateCheckbox';

function FormatterStyling(props, value, val) {
  let val1 = val || val == 0 ? val : props.value;
  let props1 = val || val == 0 ? { value: val } : props;
  console.log(val1, 'formval', props1);

  if (
    value == 'pvh_units' ||
    value == 'markdown_units' ||
    value == 'overall_planned_units' ||
    value == 'overall_markdown_units' ||
    value == 'overall_pvh_units'
  ) {
    return val1 || val1 == 0 ? toUnitDraft(props1) : '-';
  } else if (value == 'margin_perc' || value == 'overall_margin_perc') {
    return val1 || val1 == 0 ? toPercentage(props1) : '-';
  } else if (
    value == 'revenue$' ||
    value == 'aur' ||
    value == 'margin$' ||
    value == 'aum' ||
    value == 'overall_aum' ||
    value == 'overall_revenue$' ||
    value == 'overall_aur' ||
    value == 'overall_margin$'
  ) {
    return val1 || val1 == 0 ? toDollarWithlDraft(props1) : '-';
  }
  return '-';
}

class DraftStatus extends Component {
  constructor(props) {
    super(props);
    this.state = {
      toggle: false,
      currentTab: 'draft',
      selectedItems: [],
      columnOptions: [],
      selLabels: [],
      columns: [],
      statusCounts: {},
      scenarioSelected: { value: false, row: [] },
    };
  }

  componentDidUpdate(props, state) {
    if (state.currentTab !== this.state.currentTab) {
      this.props.setMarkDowns([]);
      this.props.setMDColumnHeaders({});
    }
  }
  scenarioSelectedHandler = (value, row) => {
    this.setState({ scenarioSelected: { value, row } });
  };
  onToggleTab = (tab) => {
    this.setState({
      toggle: !this.state.toggle,
      currentTab: tab,
    });
    tab === 'draft' ? this.getMarkdowns({ status: 'draft' }) : this.getMarkdowns();
  };

  onHandleSelect = (selOptions, proceed) => {
    if (!_.isEqual(this.state.selectedItems, selOptions) || proceed) {
      let headers = _.cloneDeep(this.state.columns);
      _.remove(headers, (item) => item.metric);

      selOptions.forEach((item) => {
        let newTableCols = headers.filter((a) => {
          return a.accessor === item.value;
        });
        if (newTableCols.length == 0) {
          let spliceAt = this.state.toggle ? headers.length - 2 : headers.length - 1;
          headers.splice(spliceAt, 0, {
            Header: item.label,
            accessor: item.value,
            // Cell: (props) => {
            //   return console.log(props.row.values.margin$, 'propospos');
            // },
            Cell: (props) => FormatterStyling(props, item.value),
            Aggregated: (props) => {
              let accessorName = `overall_${item.value}`;
              let val = props.row.original[accessorName];
              return FormatterStyling(props, accessorName, val);

              // return <span>{val ? Math.round(val) : '-'}</span>;
            },
            metric: true,
          });
        }
      });
      this.setState({
        columns: headers,
        selectedItems: selOptions,
      });
    }
  };

  onHandleCheckBox = (e, label, index) => {
    //adding
    if (e.target.checked) {
      if (this.state.selLabels.indexOf(label) === -1) {
        let labels = [...this.state.selLabels, label];
        let tempArr = [];
        labels.forEach((label) => {
          let columns = this.props.getColumnHeaders[label];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((label) => {
            return {
              label: label,
              value: columns[label],
            };
          });
          tempArr.push(...options);
        });

        this.setState({
          selLabels: labels,
          columnOptions: tempArr,
        });
      }
    } else {
      //removing
      let index = this.state.selLabels.indexOf(label);
      if (index !== -1) {
        let selectedLabel = _.cloneDeep(this.state.selLabels);
        selectedLabel.splice(index, 1);
        let tempArr = [];
        selectedLabel.forEach((label) => {
          let columns = this.props.getColumnHeaders[label];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((label) => {
            return {
              label: label,
              value: columns[label],
            };
          });
          tempArr.push(...options);
        });

        let selectedItemsClone = _.cloneDeep(this.state.selectedItems);
        let newSelectedItems = [];
        let labelArr = Object.keys(this.props.getColumnHeaders[label]);
        selectedItemsClone.forEach((item) => {
          if (!labelArr.includes(item.label)) {
            newSelectedItems.push(item);
          }
        });

        this.onHandleSelect(newSelectedItems);
        this.setState({
          selLabels: selectedLabel,
          columnOptions: tempArr,
          selectedItems: newSelectedItems,
        });
      }
    }
  };

  getMarkdowns = (payload) => {
    this.props.getMarkdowns(payload).then((resp) => {
      let tempStatusObj = {};
      this.props.markdowns.forEach((row) => {
        if (tempStatusObj.hasOwnProperty(row.status)) {
          tempStatusObj[row.status] += 1;
        } else {
          tempStatusObj[`${row.status}`] = 1;
        }
      });
      this.setState({
        statusCounts: tempStatusObj,
      });
      if (resp.columnHeaders && Object.keys(resp.columnHeaders).length) {
        let defaultHeader = Object.keys(resp.columnHeaders.default);

        let setDefaultColDef = draftColdef().filter(
          (col) =>
            defaultHeader.includes(col.Header) ||
            col.Header === 'Time Period' ||
            col.Header === 'Actions' ||
            col.id === 'expander' ||
            (this.state.toggle && col.accessor === 'status')
        );
        let setDefaultLabel = [];
        let setDefaultColumnOptions = [];
        let setDefaultColumnOptions1 = [];
        let setDefaultItems = [];
        let colHeaders = _.cloneDeep(resp.columnHeaders);
        let labels = Object.keys(colHeaders);
        labels.splice(labels.indexOf('fixed'), 1);
        labels.splice(labels.indexOf('default'), 1);
        for (const label of labels) {
          let labelArray = Object.keys(resp.columnHeaders[label]);
          function diff(A = labelArray, B = defaultHeader) {
            return A.filter(function (a) {
              return B.indexOf(a) !== -1;
            });
          }

          if (diff().length) {
            setDefaultColumnOptions.push(...Object.keys(resp.columnHeaders[label]));
            setDefaultColumnOptions1.push(resp.columnHeaders[label]);
            setDefaultItems.push(...diff());
            setDefaultLabel.push(label);
          }
        }
        let options = setDefaultColumnOptions.map((label) => {
          let find = setDefaultColumnOptions1 && setDefaultColumnOptions1[0][label];
          return {
            label: label,
            value: find,
          };
        });
        let selectedItems = setDefaultItems.map((label) => {
          let find = setDefaultColumnOptions1 && setDefaultColumnOptions1[0][label];
          return {
            label: label,
            value: find,
          };
        });
        setDefaultColDef.push({
          id: 'selection',
          groupByBoundary: true,
          Header: 'Actions',
          // fixed: 'left',
          Cell: (props) => {
            let { row, rows } = props;
            let mdPlan = row.original.md_plan,
              mainGrp = _.find(rows, {
                isSelected: true,
                original: { md_plan: mdPlan },
              }),
              checkboxDisabled = mainGrp && !row.isSelected ? true : false;

            return row.canExpand ? (
              <div>
                <IndeterminateCheckbox
                  row={row.id}
                  onClick={() => {
                    this.scenarioSelectedHandler(true, row);
                  }}
                  disabled={checkboxDisabled}
                  {...row.getToggleRowSelectedProps()}
                />
              </div>
            ) : null;
          },
        });
        this.setState({
          columns: setDefaultColDef,
          selLabels: setDefaultLabel,
          selectedItems: selectedItems,
          columnOptions: options,
        });
        if (selectedItems) {
          let filteredHeader = _.find(options, { accessor: selectedItems[0].value });
          if (!filteredHeader) {
            this.onHandleSelect(selectedItems, true);
          }
        }
      }
    });
  };
  render() {
    return (
      <Styles>
        <LoadingOverlay loader={this.props.getOverlayLoaderState} spinner>
          <Tabs defaultActiveKey="draft" id="uncontrolled-tab-example" onSelect={this.onToggleTab}>
            <Tab eventKey="draft" title="Draft">
              <Draft
                getMarkdowns={this.getMarkdowns}
                columns={this.state.columns}
                scenarioSelectedHandler={this.scenarioSelectedHandler}
                scenarioSelected={this.state.scenarioSelected}
                onHandleSelect={this.onHandleSelect}
                selectedItems={this.state.selectedItems}
                onHandleCheckBox={this.onHandleCheckBox}
                columnOptions={this.state.columnOptions}
                selLabels={this.state.selLabels}
              />
            </Tab>
            <Tab eventKey="status" title="Status">
              <Status
                getMarkdowns={this.getMarkdowns}
                columns={this.state.columns}
                statusCounts={this.state.statusCounts}
                onHandleSelect={this.onHandleSelect}
                selectedItems={this.state.selectedItems}
                onHandleCheckBox={this.onHandleCheckBox}
                columnOptions={this.state.columnOptions}
                selLabels={this.state.selLabels}
              />
            </Tab>
          </Tabs>
        </LoadingOverlay>
      </Styles>
    );
  }
}
const mapStateToProps = (store) => {
  return {
    getOverlayLoaderState: store.loaderReducer.overlayLoaderState,
    markdowns: store.draftStatusReducer.markdowns,
    getColumnHeaders: store.draftStatusReducer.columnHeaders,
    getStatusOptions: store.draftStatusReducer.statusOptions,
  };
};
const mapActionToProps = {
  getMarkdowns: draftStausAction.getMarkdowns,
  setMarkDowns: draftStausAction.setMarkDowns,
  setMDColumnHeaders: draftStausAction.setMDColumnHeaders,
};

export default connect(mapStateToProps, mapActionToProps)(DraftStatus);

const Styles = styled.div`
  li.nav-item {
    padding-right: 0;
  }

  .nav-tabs .nav-item.show .nav-link,
  .nav-tabs .nav-link.active {
    background: none;
    border-bottom: 2px solid #44667a;
  }
