import React, { Component } from 'react'
import { Bar } from "react-chartjs-2";

let chartOptions = {
  plugins: {
    datalabels: {
       display: false
    }
 },
  hover: {
    mode: "nearest",
  },
  scales: {
    xAxes: [
      {
        gridLines: {
          display: false,

        },
        ticks: {
          fontSize: 10,
          opacity: 0.7,
         },

        barPercentage: 1.0,
      }
    ],
    yAxes: [
      {
        gridLines: {
          display: false,
          // drawBorder: false,
        },
      }
    ]
  },
  legend: {
    display: true,
    position: 'bottom',
    labels: {
      boxWidth: 12
  },
    fontFamily: "Helvetica",
    fontSize: "12px",

  },
  responsive: true,
  maintainAspectRatio: true,
}
const data = {
  labels: ["January", "February", "March", "April", "May", "June", "July"],
  datasets: [
    {
      label: "My First dataset",
      backgroundColor: "rgba(45,105,235,0.7)",
      borderColor: "rgba(45,105,235,0.8)",
      borderWidth: 1,
      hoverBackgroundColor: "rgba(255,99,132,0.4)",
      hoverBorderColor: "rgba(255,99,132,1)",
      data: [65, 59, 80, 81, 56, 55, 40]
    }
  ]
};

export class PerformanceRevenue extends Component {
  render() {
    return (
        <div className="RevenueTab">

        <div className="row">
          <div className="col-md-6">
          <h4 className="promoHeading">Current Year</h4>
            <div className="cards MarginCharts">
                    <div>
                    <Bar data={data} options={chartOptions}/>
                    </div>
            </div>
          </div>
          <div className="col-md-6">
          <h4 className="promoHeading">Last Year</h4>
            <div className="cards RevenueCharts">
                    <div>
                    <Bar data={data} options={chartOptions}/>
                    </div>
            </div>
          </div>
        </div>

      </div>
    )
  }
}

export default PerformanceRevenue
