import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { AgGridReact } from 'ag-grid-react/lib/agGridReact';
import { isEqual, cloneDeep } from 'lodash';
import LoadingOverlay from 'react-loading-overlay';
import PageLoader from '../../utils/Loader';

class GlobalPromosmarkdown extends Component {
  state = {
    data: [],
  };

  componentDidUpdate(prevProps) {
    if (!isEqual(prevProps.promoData, this.props.promoData)) {
      const modifiedData = this.props.promoData.map((dataItem) => {
        return {
          ...dataItem,
          rowHeight: 70,
        };
      });
      this.setState({
        data: modifiedData,
      });
    }
  }

  onGridReady = (params) => {
    this.gridApi = params.api;
    this.gridColumnApi = params.columnApi;
    const updateData = (data) => {
      const modifiedData = data.map((dataItem) => {
        return {
          ...dataItem,
          rowHeight: 70,
        };
      });
      this.setState({
        data: modifiedData,
      });
    };

    updateData(this.props.promoData);
    params.api.sizeColumnsToFit();
  };

  render() {
    let colDef = {
      header: [
        {
          headerName: 'Promo Construct',
          field: 'description',
          resizable: true,
        },
        {
          headerName: 'Applicability',
          field: 'type',
          resizable: true,
        },
        {
          headerName: 'Start Date',
          field: 'startdate',
          resizable: true,
        },
        {
          headerName: 'End Date',
          field: 'enddate',
          resizable: true,
        },
      ],
    };

    return (
      <PageLoader>
        <div className="calendarContainer" id="main-calendar-container">
          <div className="promo">
            <div className="card CalendarMain promoCalendarDiv1" id="promocalendar-maincontainer">
              <h4 className="promoHeading mb-0">
                <span>Markdown Promos</span>
              </h4>
              <hr />
              <div className="row pipelineCopydiv">
                <div className="col-md-12">
                  <div className="row" style={{ marginLeft: '12px' }}>
                    <div className="col-md-4">
                      <span className="promoNameText row">
                        Markdown Name:
                        {this.props.markdownName ? (
                          <p className="promonameResult">{this.props.markdownName}</p>
                        ) : null}
                      </span>
                    </div>
                    <div className="col-md-3">
                      <span className="promoNameText row" style={{ paddingLeft: '2%' }}>
                        Start Date:
                        {this.props.markdownStartDate ? (
                          <p className="promonameResult">{this.props.markdownStartDate}</p>
                        ) : null}
                      </span>
                    </div>
                    <div className="col-md-3">
                      <span className="promoNameText row" style={{ paddingLeft: '2%' }}>
                        End Date:
                        {this.props.markdownEndDate ? (
                          <p className="promonameResult">{this.props.markdownEndDate}</p>
                        ) : null}
                      </span>
                    </div>
                    {/* <div className="col-md-2">
                      <span
                        className="promoNameText row"
                        style={{ paddingLeft: "2%" }}
                      >
                        # of days:
                        {diffDays ? (
                          <p className="promonameResult">
                            {diffDays ? diffDays : ""}
                          </p>
                        ) : null}
                      </span>
                    </div> */}
                  </div>
                </div>
              </div>
              <div className="pipelineGrid" style={{ padding: '0px 19px' }}>
                <div
                  id="myGridForPipeline"
                  style={{
                    // height: "calc(100vh - 10px) !important",
                    height: '300px',
                    width: '100%',
                  }}
                  className="ag-theme-balham">
                  <AgGridReact
                    columnDefs={colDef.header}
                    onGridReady={this.onGridReady}
                    pagination
                    paginationAutoPageSize
                    rowData={this.state.data}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </PageLoader>
    );
  }
}

const mapStateToProps = (state) => ({
  showLoading: state,
});

export default connect(mapStateToProps)(withRouter(GlobalPromosmarkdown));
