import React, { Component } from 'react';
import DataTable from '../../services/reactTable';
import excludeStoreColdef from './excludeStoreColdef';
import cloneDeep from 'clone-deep';
import styled from 'styled-components';
import _ from 'lodash';

class ExcludeStoreTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // attributes: ['Product Information', 'Sales', 'Inventory', 'Margin'],
      columnOptions: [],
      selLabels: [],
      columns: [],
      selected: {},
      limitBtnDisable: true,
    };
  }
  limitClearnceHandler = () => {
    let selectedRowId = this.state.selected;
    let selectedRows = {};
    _.map(selectedRowId, (item) => {
      selectedRows[item.rowNum] = true;
    });

    this.props.excludeStoreValHandler(selectedRowId, selectedRows);
    this.props.showTablePopup();
  };
  onRowClick = (rowInfo) => {
    if (rowInfo && !Object.keys(rowInfo).length) {
      this.setState({
        selected: {},
      });
      return;
    }
    let selectedRow = [];
    if (Object.keys(rowInfo).length) {
      Object.keys(rowInfo).forEach((row) => {
        selectedRow.push({ ...this.props.storeData[row], rowNum: row });
      });
    }
    this.setState({
      selected: selectedRow,
    });
    if (Object.keys(selectedRow).length) {
      this.setState({ limitBtnDisable: false });
    } else {
      this.setState({ limitBtnDisable: _.isEmpty(this.props.excludedRows) ? true : false });
    }
  };

  onHandleCheckBox = (e, label, index) => {
    //adding
    if (e.target.checked) {
      if (this.state.selLabels.indexOf(label) === -1) {
        let labels = [...this.state.selLabels, label];
        let tempArr = [];
        labels.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((label) => {
            return {
              label: label,
              value: columns[label],
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selLabels: labels,
          columnOptions: tempArr,
        });
      }
    } else {
      //removing
      let columns = this.props.columnHeaders[label];
      let tabelColumns = cloneDeep(this.state.columns);
      columns.forEach((col) => {
        tabelColumns.forEach((tabelCol, index) => {
          if (tabelCol.Header === col) {
            tabelColumns.splice(index, 1);
          }
        });
      });
      this.setState({
        columns: tabelColumns,
      });
      let index = this.state.selLabels.indexOf(label);
      if (index !== -1) {
        let selectedLabel = cloneDeep(this.state.selLabels);
        selectedLabel.splice(index, 1);
        let tempArr = [];
        selectedLabel.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((label) => {
            return {
              label: label,
              value: columns[label],
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selLabels: selectedLabel,
          columnOptions: tempArr,
        });
      }
    }
  };
  onHandleSelect = (options) => {
    let headers = cloneDeep(this.state.columns);
    // let selectedOptions = this.state.selectedOptions;
    options.forEach((da) => {
      let newCols = headers.filter((a) => {
        return a.accessor === da.value;
      });
      if (newCols.length == 0) {
        headers.push({
          Header: da.label,
          accessor: da.value,
        });
      }
    });
    this.setState({
      columns: headers,
    });
  };
  componentDidMount() {
    this.setState({
      columns: excludeStoreColdef(),
      limitBtnDisable: _.isEmpty(this.props.excludedRows) ? true : false,
    });
  }
  render() {
    return (
      <div className="createMd pl-2 pr-2">
        {/* <div className="card mb-4">
          <ColumnSelection
            onHandleCheckBox={this.onHandleCheckBox}
            columnHeaders={this.props.columnHeaders}
            onHandleSelect={this.onHandleSelect}
            selLabels={this.state.selLabels}
            columnOptions={this.state.columnOptions}
            selectedItems={this.state.selectedItems}
          /> */}
        <div style={{ padding: '1rem' }}>
          <DataTable
            isPagination={true}
            isSort={this.props.storeData.length > 1}
            visibleColumns="onlyRowSelection"
            columns={this.state.columns}
            data={this.props.storeData}
            // getRowProps={(rowInfo) => ({
            //   onClick: () => this.onRowClick(rowInfo),
            // })}
            selectRow={(data) => this.onRowClick(data)}
            selectedRows={this.props.excludedRows}
          />
        </div>
        <div class="mt-3 mb-3" style={{ display: 'flex', justifyContent: 'center' }}>
          <button
            className="btn btn-primary biconbuttonFilter"
            disabled={this.state.limitBtnDisable}
            onClick={this.limitClearnceHandler}>
            Exclude
          </button>
        </div>
      </div>
      // </div>
    );
  }
}

const Styles = styled.div`
  padding: 1rem;
  display: flex;
  flex-direction: row;
  justify-content: flex-start;

  .custom-control {
    line-height: 1.5rem;
    margin: 0 1rem;
  }

  .custom-control-label::before {
    top: 0.5rem;
  }
  .custom-control-label::after {
    top: 0.5rem;
  }
  .custom-checkbox label {
    vertical-align: -webkit-baseline-middle;
  }
`;

export default ExcludeStoreTable;
