import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import Select from 'react-select';
import Filters from '../../../services/filters';
import DataTable from '../../../services/reactTable';
import '../../Configuration/StoreConfigContainers/StoreConfig.css';
import excelIcon from '../../../images/leftPanel/status.svg';

const edlpData = [
  { label: 'EDLP', value: 'EDLP' },
  { label: 'NON EDLP', value: 'NON EDLP' },
];

const styleData = [
  {
    label: 'M NK BRTHE CHLLGR TOP SS',
    value: 'M NK BRTHE CHLLGR TOP SS',
  },
  {
    label: 'M NK DRY MILER TOP SS SSNL GX',
    value: 'M NK DRY MILER TOP SS SSNL GX',
  },
  {
    label: 'M NK TOP SL HOODIE SSNL GX',
    value: 'M NK TOP SL HOODIE SSNL GX',
  },
  {
    label: 'JORDAN BELL CAP',
    value: 'JORDAN BELL CAP',
  },
  {
    label: 'JORDAN CARBON FIBER FITTED',
    value: 'JORDAN CARBON FIBER FITTED',
  },
];
const header = [
  {
    Header: 'Name',
    accessor: 'name',
  },
  {
    Header: 'Value',
    accessor: 'val',
  },
];
const tableData = [
  { name: 'Style Id', val: '' },
  { name: 'Department', val: '' },
  { name: 'Sub-Class', val: '' },
  { name: 'Class', val: '' },
  { name: 'Style Name', val: '' },
  { name: 'Sub-Department', val: '' },
];

export class CreateStyle extends Component {
  state = {
    styleId: '',
    styleName: '',
    department: '',
    edlpValue: '',
    subDepartment: '',
    class: '',
    subClass: '',
    refStyle: '',
    replacementStyle: '',
    mapping: 'reference',
  };
  onCancelButtonHandler = () => {
    // this.setState({ allStores: [] });
    this.props.history.goBack();
  };
  onCreateStyleHandler = () => {

  }
  render() {
    return (
      <div className="card p-2">
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <h4 className="font-size-14  pad-10 markdown-heading">
            Store Configuration/
            <span className="font-bold color-blue">Map Style</span>
          </h4>
          <div>
            <button className="btn btn-primary" onClick={this.onCancelButtonHandler}>
              Back
            </button>
          </div>
        </div>
        <hr
          className="mb-2"
          style={{
            marginTop: '-4px',
          }}
        />
        <div>
          <Filters
            hideStoreClusterFilter
            hidefilterButton
            hierarchy={['hierarchy1', 'hierarchy2', 'hierarchy3', 'hierarchy4']}
            // onFilter={this.onFilter}
          />
          <div className="row">
            <div className="col-md-2 mt-2" style={{ marginLeft: '39px' }}>
              <label className="mb-1 mt-3">Search by Style Id</label>
              <Select
                id="store-group-select"
                placeholder="Style Id"
                // options={this.props.storeGroups}
                // value={this.state.storeGroup}
                // onChange={this.onStoreGroupChangeHandler}
              />
            </div>
            <div className="col-md-2 mt-5">
              <button
                // onClick={() => this.onFilter}
                className="col-md-5 btn btn-primary btn-filter">
                <span>
                  <i className="fa fa-filter"></i>&nbsp; Filter
                </span>
              </button>
              <button
                className="col-md-6 offset-1 btn btn-primary btn-filter btn-filter-reset"
                // onClick={this.reset}
              >
                <span>
                  <i className="fa fa-repeat"></i>&nbsp; Reset
                </span>
              </button>
            </div>
          </div>

          {this.state.department && this.state.department.value === 'GOLF BALLS' && (
            <div className="row p-2" style={{ justifyContent: 'center' }}>
              <div className="col-md-4">
                <>
                  <label className="input-label">
                    {this.state.department.value === 'GOLF BALLS' && 'EDLP Flag'}
                  </label>
                  {this.state.department.value === 'GOLF BALLS' && (
                    <Select
                      // isDisabled={this.props.loading}
                      name="edlp"
                      placeholder="Select edlp"
                      // allowSelectAll
                      options={edlpData}
                      value={this.state.edlpValue}
                      onChange={(val) => this.setState({ edlpValue: val})}
                    />
                  )}
                </>
              </div>
              <div className="col-md-3"></div>
            </div>
          )}
        </div>
        <hr />
        <div style={{ height: '372px', width: '64%', margin: '0 auto' }}>
          <DataTable columns={header} data={tableData} />
        </div>
        <div>
          <div>Style mapping</div>
          <div className="row p-4">
            <input
              className="align-self-center"
              id="reference"
              type="radio"
              name="mapping"
              value="reference"
              checked={this.state.mapping === 'reference'}
              onChange={(e) => this.setState({ mapping: e.target.value})}
            />
            &nbsp;&nbsp;
            <label className="align-self-center" htmlFor="reference">
              Refence style
            </label>
            <div className="col-md-2 pr-0 align-self-center ref-style-dropdown">
              <Select
                name="refStyle"
                placeholder="Select Refence Style"
                options={styleData}
                value={this.state.refStyle}
                onChange={(val) => this.setState({ refStyleChange: val})}
              />
            </div>
          </div>
          <div className="row p-4">
            <input
              className="align-self-center"
              id="repStyle"
              type="radio"
              name="mapping"
              value="replacement"
              checked={this.state.mapping === 'replacement'}
              onChange={(e) => this.setState({ mapping: e.target.value})}
            />
            &nbsp;&nbsp;
            <label className="align-self-center" style={{ margin: '0px' }} htmlFor="repStyle">
              Replacementstyle
            </label>
            <div className="col-md-4 align-self-center">
              <Select
                name="repStyle"
                placeholder="Select Replacement Style"
                options={styleData}
                value={this.state.replacementStyle}
                onChange={(val) => this.setState({ replacementStyle: val})}
              />
            </div>
            <div className="col-md-4">*Changes will be reflected within 24 hrs</div>
          </div>
        </div>
        <hr />

        <div className="mb-3 mt-4" style={{ textAlign: 'center' }}>
          <button
            style={{ width: '7%', fontSize: '15px' }}
            className="btn btn-primary"
            onClick={() => this.onCreateStyleHandler()}>
            Create
          </button>
          <button
            style={{ width: '7%', fontSize: '15px' }}
            className="btn ml-1 cancel-button"
            onClick={this.onCancelButtonHandler}>
            Cancel
          </button>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (store) => {
  return {
    selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
  };
};
const mapActionsToProps = {};
export default connect(mapStateToProps, mapActionsToProps)(withRouter(CreateStyle));
