import React, { useReducer } from 'react';
import Select from 'react-select';
import excelIcon from '../../../images/leftPanel/status.svg';

const deptData = [
  { label: 'MENS ATHLETIC APPAREL', value: 'MENS ATHLETIC APPAREL' },
  { label: 'GOLF BALLS', value: 'GOLF BALLS' },
  { label: 'SOCKS', value: 'SOCKS' },
  { label: 'MENS  OUTERWEAR', value: 'MENS  OUTERWEAR' },
];

const subDeptData = [
  { label: 'ALL', value: 'ALL' },
  { label: 'NIKE', value: 'NIKE' },
  { label: 'Adidas', value: 'Adidas' },
  // { label: 'ADIDAS', value: 'ADIDAS' },
  // { label: 'WOMENS HEAVYWEIGHT', value: 'WOMENS HEAVYWEIGHT' },
  // { label: 'NIKE', value: 'NIKE' },
];

const classData = [
  { label: 'ALL', value: 'ALL' },
  { label: 'N RUNNING', value: 'N RUNNING' },
  { label: 'A JORDAN', value: 'A JORDAN' },
  // { label: 'A FLEECE', value: 'A FLEECE' },
  // { label: 'INSULATED', value: 'INSULATED' },
  // { label: 'N FLEECE', value: 'N FLEECE' },
];

const subClassData = [
  { label: 'ALL', value: 'ALL' },
  { label: 'N RUNNING TOPS', value: 'N RUNNING TOPS' },
  { label: 'A JORDAN SL ACCESSORIES', value: 'A JORDAN SL ACCESSORIES' },
  // { label: 'A FLEECE TOPS', value: 'A FLEECE TOPS' },
  // { label: 'COLUMBIA', value: 'COLUMBIA' },
  // { label: 'N FLEECE BOTTOMS', value: 'N FLEECE BOTTOMS' },
];

const edlpData = [
  { label: 'EDLP', value: 'EDLP' },
  { label: 'NON EDLP', value: 'NON EDLP' },
];

const outerwearData = [
  { label: 'ALL', value: 'ALL' },
  { label: 'COLD', value: 'COLD' },
  { label: 'VERY COLD', value: 'VERY' },
];

const styleData = [
  {
    label: 'M NK BRTHE CHLLGR TOP SS',
    value: 'M NK BRTHE CHLLGR TOP SS',
  },
  {
    label: 'M NK DRY MILER TOP SS SSNL GX',
    value: 'M NK DRY MILER TOP SS SSNL GX',
  },
  {
    label: 'M NK TOP SL HOODIE SSNL GX',
    value: 'M NK TOP SL HOODIE SSNL GX',
  },
  {
    label: 'JORDAN BELL CAP',
    value: 'JORDAN BELL CAP',
  },
  {
    label: 'JORDAN CARBON FIBER FITTED',
    value: 'JORDAN CARBON FIBER FITTED',
  },
];

const initialState = {
  styleId: '',
  styleName: '',
  department: '',
  edlpValue:'',
  subDepartment: '',
  class: '',
  subClass: '',
  refStyle: '',
  replacementStyle: '',
  mapping: 'reference',
};

function reducer(state, action) {
  switch (action.type) {
    case 'styleIdChange':
      return { ...state, styleId: action.value };
    case 'styleNameChange':
      return { ...state, styleName: action.value };
    case 'departmentChange':
      return { ...state, department: action.value };
    case 'edplChange':
      return { ...state, edlpValue: action.value };
    case 'subDepartmentChange':
      return { ...state, subDepartment: action.value };
    case 'classChange':
      return { ...state, class: action.value };
    case 'subClassChange':
      return { ...state, subClass: action.value };
    case 'refStyleChange':
      return { ...state, refStyle: action.value };
    case 'replacementStyleChange':
      return { ...state, replacementStyle: action.value };
    case 'mapping':
      return { ...state, mapping: action.value };
    default:
      throw new Error();
  }
}

const CreateStyle = props => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const {
    __Style_details,
    __Style_ID,
    __Sub_department,
    __Style_name,
    __Class,
    __Department,
    __Sub_class,
    __Style_mapping,
    __Refence_style,
    __Replacement_style,
    __Create } = props.translation
  return (
    <div className="card p-2">
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <div>
          Style Configuration /
          <span className="font-bold color-blue">
            {props.isEditStyle ? 'Edit' : 'Create New'} Style
          </span>
        </div>
        <div>
          <button className="btn btn-primary" onClick={props.onCancelButtonHandler}>
            Back
          </button>
        </div>
      </div>
      <hr />
      <div>
        <div className="row justify-content-between">
          <div className="col-md-2">{__Style_details}</div>
          <div className="col-md-2 text-right">
            <button type="button" className="btn btn-primary" onClick={() => {}}>
              Upload &nbsp;&nbsp;
              <span>
                <img className="img-icons-create-promo" src={excelIcon} alt="" />
              </span>
            </button>
          </div>
        </div>
        <div className="row p-2" style={{ justifyContent: 'center' }}>
          <div className="col-md-4">
            <label className="input-label">
              <span style={{ color: 'red' }}>*</span>{__Style_ID}
            </label>
            <input
              type="text"
              value={state.styleId}
              onChange={e => dispatch({ type: 'styleIdChange', value: e.target.value })}
            />
          </div>
          <div className="col-md-3">
          <label className="input-label">{__Sub_department}</label>
            <Select
              name="subDepartment"
              placeholder="Select Sub Department"
              options={subDeptData}
              value={state.subDepartment}
              onChange={val => dispatch({ type: 'subDepartmentChange', value: val })}
            />
          </div>
        </div>
        <div className="row p-2" style={{ justifyContent: 'center' }}>
          <div className="col-md-4">
            <label className="input-label">
              <span style={{ color: 'red' }}>*</span>{__Style_name}
            </label>
            <input
              type="text"
              value={state.styleName}
              onChange={e => dispatch({ type: 'styleNameChange', value: e.target.value })}
            />
          </div>
          <div className="col-md-3">
          <label className="input-label">{__Class}</label>
            <Select
              name="class"
              placeholder="Select Class"
              options={classData}
              value={state.class}
              onChange={val => dispatch({ type: 'classChange', value: val })}
            />
          </div>
        </div>
        <div className="row p-2" style={{ justifyContent: 'center' }}>
          <div className="col-md-4">
          <label className="input-label">{__Department}</label>
            <Select
              name="department"
              placeholder="Select Department"
              options={deptData}
              value={state.department}
              onChange={val => dispatch({ type: 'departmentChange', value: val })}
            />
          </div>
          <div className="col-md-3">
          <label className="input-label">{__Sub_class}</label>
            <Select
              name="subClass"
              placeholder="Select Sub Class"
              options={subClassData}
              value={state.subClass}
              onChange={val => dispatch({ type: 'subClassChange', value: val })}
            />
          </div>
        </div>
        {state.department && state.department.value === 'GOLF BALLS' && (
          <div className="row p-2" style={{ justifyContent: 'center' }}>
          <div className="col-md-4">
            <>
              <label className="input-label">
                {state.department.value === 'GOLF BALLS' && (
                  'EDLP Flag'
                )}                
              </label>
                  { state.department.value === 'GOLF BALLS' && (
              <Select
                // isDisabled={this.props.loading}
                name="edlp"
                placeholder="Select edlp"
                // allowSelectAll
                options={edlpData}
                value={state.edlpValue}
                onChange={val => dispatch({ type: 'edplChange', value: val })}
              />
                  )}
            </>
        </div>
        <div className="col-md-3"></div>
        </div>
          )}
      </div>
      <hr />
      <div>
      <div>{__Style_mapping}</div>
        <div className="row p-4">
          <input
          className="align-self-center"
            id="reference"
            type="radio"
            name="mapping"
            value="reference"
            checked={state.mapping === 'reference'}
            onChange={e => dispatch({ type: 'mapping', value: e.target.value })}
          />&nbsp;&nbsp;
          <label className="align-self-center" style={{margin:"0px"}} htmlFor="reference">{__Refence_style}</label>
          <div className="col-md-2 align-self-center">
            <Select
              name="refStyle"
              placeholder="Select Refence Style"
              options={styleData}
              value={state.refStyle}
              onChange={val => dispatch({ type: 'refStyleChange', value: val })}
            />
          </div>
        </div>
        <div className="row p-4">
          <input
          className="align-self-center"
            id="repStyle"
            type="radio"
            name="mapping"
            value="replacement"
            checked={state.mapping === 'replacement'}
            onChange={e => dispatch({ type: 'mapping', value: e.target.value })}
          />&nbsp;&nbsp;
          <label className="align-self-center" style={{margin:"0px"}} htmlFor="repStyle">{__Replacement_style}</label>
          <div className="col-md-4 align-self-center">
            <Select
              name="repStyle"
              placeholder="Select Replacement Style"
              options={styleData}
              value={state.replacementStyle}
              onChange={val => dispatch({ type: 'replacementStyleChange', value: val })}
            />
          </div>
          <div className="col-md-4">*Changes will be reflected within 24 hrs</div>
        </div>
      </div>
      <hr />
      <div style={{ textAlign: 'center' }}>
        <button className="btn btn-primary" onClick={() => props.onCreateStyleHandler(state)}>
          {__Create}
        </button>
        <button className="btn ml-1 cancel-button" onClick={props.onCancelButtonHandler}>
          Cancel
        </button>
      </div>
    </div>
  );
};

export default CreateStyle;
