import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import StyleConfigFilter from '../StyleConfigFilter/StyleConfigFilter';
import CreateStyleGroup from './CreateStyleGroup';
import DataTable from '../../../services/reactTable';

import './StyleConfig.css';

const Tabledata = [
  {
    style_group: 'aaaaa_aaa',
    subRows: [
      {
        id: '205-1-2-1-1C300WMBHGN3',
        name: 'BH GOLD 300 WIN 180GR ACCUBOND',
      },
      {
        id: '205-1-2-1-1C308BHGN6',
        name: 'BH GOLD 308 WIN 150 GR SST 20R',
      },
    ],
  },
];
export class Config extends Component {
  state = {
    styleTable: 'styleGroup',
    showEDLP: true,
    styleGroupEdit: false,
    header: [
      {
        // Build our expander column
        id: 'expander', // Make sure it has an ID
        // Header: ({ getToggleAllRowsExpandedProps, isAllRowsExpanded }) => (
        //   <span {...getToggleAllRowsExpandedProps()}>{isAllRowsExpanded ? 'ðŸ‘‡' : 'ðŸ‘‰'}</span>
        // ),
        Header: '',
        Cell: ({ row }) => {
          // Use the row.canExpand and row.getToggleRowExpandedProps prop getter
          // to build the toggle for expanding a row
          return row.canExpand ? (
            <span
              {...row.getToggleRowExpandedProps({
                style: {
                  // We can even use the row.depth property
                  // and paddingLeft to indicate the depth
                  // of the row
                  paddingLeft: `${row.depth * 2}rem`,
                },
              })}>
              {row.isExpanded ? (
                <i class="fa fa-angle-down expanded-icon-size" aria-hidden="true"></i>
              ) : (
                <i class="fa fa-angle-right expanded-icon-size" aria-hidden="true"></i>
              )}
            </span>
          ) : null;
        },
      },
      {
        Header: 'Style Group',
        accessor: 'style_group',
      },
      {
        Header: 'Style ID',
        accessor: 'id',
      },
      {
        Header: 'Style Description',
        accessor: 'name',
      },
      {
        Header: 'Edit Style',
        Cell: ({ row }) => {
          return row.id === '0' ? (
            <i
              className="fa fa-pencil color-blue"
              onClick={() => {
                this.setState({ styleGroupEdit: true });
              }}></i>
          ) : null;
        },
      },
    ],
  };

  styleGroupEditHandler = () => this.setState({ styleGroupEdit: false})

  renderTable = (styleName, data) => {
    if (styleName === 'style') {
      return <DataTable columns={this.state.header} data={Tabledata} />;
    }
    if (styleName === 'styleGroup') {
      return <DataTable columns={this.state.header} data={Tabledata} />;
    }
    return null;
  };

  onStyleTypeChangeHandler = (value) => {
    this.setState({
      styleTable: value,
    });
  };

  render() {
    return (
      <>
        {!this.state.styleGroupEdit ? (
          <div className="card p-2">
            <div className="mt-1" style={{ display: 'flex', justifyContent: 'space-between' }}>
              <h4 className="font-size-14  pad-10 markdown-heading">Style Configuration</h4>
              <div>
                <button
                  className="btn btn-primary"
                  onClick={() => {
                    this.props.history.push(`/vis/Markdown/configuration/style/group`);
                  }}>
                  Create Style Group
                </button>
                <button
                  className="btn btn-primary ml-1"
                  onClick={() => {
                    this.props.history.push(`/vis/Markdown/configuration/style/create`);
                  }}>
                  Map Style
                </button>
              </div>
            </div>
            <hr
              className="mb-2 mt-1"
              style={{
                marginTop: '-4px',
              }}
            />
            <StyleConfigFilter
              from="styleConfig"
              styleType={this.state.styleTable}
              onStyleTypeChangeHandler={this.onStyleTypeChangeHandler}
              showEDLP={this.state.showEDLP}
            />
            <hr />
            <div style={{ height: '372px', width: '64%', margin: '0 auto' }}>
              {this.renderTable(this.state.styleTable)}
            </div>
          </div>
        ) : (
          <CreateStyleGroup
            styleGroupEdit={true}
            styleGroupEditHandler={this.styleGroupEditHandler}
          />
        )}
      </>
    );
  }
}
const mapStateToProps = (store) => {
  return {
    styleConfigTableData: store.styleReducer.styleConfigTableData,
  };
};
const mapDispatchToProps = {};
export default connect(mapStateToProps, mapDispatchToProps)(withRouter(Config));
