const initialState = {
  inventoryInfoData: '',
  cadenceData: [],
  csv_names: [],
  cadenceInput: [],
  customStyleDetails: [],
  skus: {},
  editPlanDetails: [],
  simulationDataBySocket: [],
  simulationId: [],
  weekInfo: null,
  offerTypeDetails: '',
  //   {
  //     hierarchy: {
  //       hierarchy1: 'Apparel',
  //       hierarchy2: 'WNS',
  //       hierarchy3: 'Training & Fitness',
  //       hierarchy4: 'Not Available',
  //       hierarchy5: 'AWTK',
  //     },
  //     offer_type: 'fixed_price',
  //     weighted_avg_price: 50,
  //     bucket: '>20&<=100',
  //     interval: 5,
  //     min_price: 5,
  //     max_price: 50,
  //   },
  //   {
  //     hierarchy: {
  //       hierarchy1: 'Apparel',
  //       hierarchy2: 'WNS',
  //       hierarchy3: 'Training & Fitness',
  //       hierarchy4: 'Not Available',
  //       hierarchy5: 'AWTK',
  //     },
  //     offer_type: 'percent_off',
  //     weighted_avg_price: 50,
  //     bucket: '>20&<=100',
  //     interval: 5,
  //     min_price: 0,
  //     max_price: 95,
  //   },
  // ],
  discountOff: { min: '', max: '', interval: '' },
  discountPP: { min: '', max: '', interval: '' },
  columnHeaders: {
    default: {
      Bin: 'bins',
      'SKU ID': 'SKU_Id',
      'Style Description': 'style_description',
      'Revenue $': 'revenue',
      'Planned units': 'planned_units',
      'Markdown units': 'units',
      'AUR $': 'aur',
      'Margin %': 'margin_percentage',
      'Margin $': 'margin',
      'AUM $': 'aum',
      'ST %': 'sellThrough',
      MD: 'scenario',
    },
    fixed: {
      Bin: 'bins',
      'SKU ID': 'SKU_Id',
      'Style Description': 'style_description',
      'ST %': 'sellThrough',
      'Margin %': 'margin_percentage',
      MD: 'scenario',
    },
    Sales: {
      'Revenue $': 'revenue',
      'Planned units': 'planned_units',
      'Markdown units': 'units',
      'AUR $': 'aur',
    },
    Margin: {
      'Margin $': 'margin',
      'AUM $': 'aum',
    },
  },
};

export default function (state = initialState, action) {
  switch (action.type) {
    case 'GET_INVENTORY_INFO':
      return {
        ...state,
        inventoryInfoData: action.payload,
      };
    case 'RESET_INVENTORY_INFO_DATA':
      return {
        ...state,
        inventoryInfoData: '',
        cadenceData: [],
        csv_names: [],
      };
    case 'GET_OFFER_DETAILS':
      return {
        ...state,
        offerTypeDetails: action.payload,
        //offerTypeDetails: initialState.offerTypeDetails,
      };
    case 'SET_OFFER_DETAILS':
      return {
        ...state,
        discountOff: action.discountOff,
        discountPP: action.discountPP,
      };
    case 'SET_CADENCE_INPUT':
      return {
        ...state,
        cadenceInput: action.payload,
      };
    case 'GET_CADENCE_DATA':
      return {
        ...state,
        cadenceData: action.payload,
        csv_names: action.csv_names,
        skus: action.skus,
        savedData: action.savedData,
        weekInfo: action.weekInfo,
      };
    case 'GET_SKUS_AND_CSV':
      return {
        ...state,
        csv_names: action.csv_names,
        skus: action.skus,
      };
    case 'MARKDOWN_SAVED_STATUS':
      return {
        ...state,
        status: action.status,
      };
    case 'GET_COLUMNHEADERS':
      return {
        ...state,
        columnHeaders: {
          ...state.columnHeaders,
        },
      };
    case 'GET_EDITPLAN_DETAILS':
      return {
        ...state,
        editPlanDetails: action.payload,
      };
    case 'SET_CUSTOM_STYLE_INFO':
      return {
        ...state,
        customStyleDetails: action.payload,
      };
    case 'SET_SIMULATION_DATA_BY_SOCKET':
      return {
        ...state,
        simulationDataBySocket: action.payload,
      };

    case 'SET_SIMULATION_ID':
      return {
        ...state,
        simulationId: action.payload,
      };
    default:
      return {
        ...state,
      };
  }
}
