import { toast } from 'react-toastify';

const mainReducerDefaultState = {
  overlayLoaderState: false,
  loaderText: 'Loading...',
  errorText: '',
};

export default (state = mainReducerDefaultState, action) => {
  console.log(action.payload && action.payload.errorText, 'aaaaa');
  switch (action.type) {
    case 'LOADER_STATE':
      return {
        ...state,
        overlayLoaderState: action.payload.status,
        loaderText: action.payload.text,
        errorText: toast.error(action.payload.errorText),
      };
    default:
      return state;
  }
};
