import React, { Component } from 'react';
import { connect } from 'react-redux';
import DraftAction from '../../../actions/draftStausFinalizeAction';
import Filters from '../../../services/filters';
import DataTable from '../../../services/reactTable';
import Modal from '../../../services/modal';
import ColumnSelection from '../../columnSelection';
import StatusListCount from './statusListCount';
import Select from '../../../utils/reactDropDownSelect';
// import statusColDef from '../status/coldef';
import styled from 'styled-components';
import _ from 'lodash';
import '../draftStatus.css';
import { cloneDeep } from 'lodash';
import { toast } from 'react-toastify';
import NoData from '../../noTableData';
import moment from 'moment';

class Status extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectStatusValues: [],
      comment: '',
      isComment: false,
      rowDetails: {},
      selectedLabels: [],
      selectedItems: [],
      columnOptions: [],
      selLabels: [],
      columns: [],
    };
  }

  onCellClick = (row) => {
    if (row.value === 'rejected') {
      this.setState({
        isComment: true,
        rowDetails: row.row.original,
      });
    }
  };
  setComment = (e) => {
    this.setState({
      comment: e.target.value,
    });
  };

  onCloseComment = () => {
    this.setState({
      isComment: false,
      rowDetails: {},
    });
  };
  onSaveComment = () => {
    let tableData = cloneDeep(this.props.statusMarkdowns);
    tableData.forEach((row) => {
      if (
        this.state.rowDetails.md_plan === row['md_plan'] &&
        this.state.rowDetails.scenario === row['scenario']
      ) {
        row.comment = this.state.comment;
      }
    });
    this.props.setStatusData(tableData);
    this.setState({
      isComment: false,
      rowDetails: {},
    });
  };

  findStatusColor = (status, color) => {
    switch (status) {
      case 'high':
        return color.high;
      case 'medium':
        return color.medium;
      case 'low':
        return color.low;
      default:
        break;
    }
  };

  bgmCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let bgCol = {
      high: '#F8E8E8',
      medium: '#FCF3D1',
      low: '#E8F8F5',
    };
    return this.findStatusColor(status, bgCol);
  };
  txtCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let txtCol = {
      high: '#D38485',
      medium: '#F0C53A',
      low: '#66CFBA',
    };
    return this.findStatusColor(status, txtCol);
  };
  ////////// handling reset issues rising bcs of tabs start /////////////////////////
  mandatoryFiltersVal = ({ filters, startDate, endDate }) => {
    return !_.isEmpty(filters.hierarchy1) && !_.isEmpty(filters.hierarchy2) && startDate && endDate;
  };
  onFilter = ({ filters, startDate, endDate }) => {
    if (!this.mandatoryFiltersVal({ filters, startDate, endDate })) {
      toast.error(`Please select mandatory filters`, {
        autoClose: 15000,
        hideProgressBar: true,
      });
      return;
    }
    let payload = {
      filters: {
        hierarchy1: filters.hierarchy1,
        hierarchy2: filters.hierarchy2,
        hierarchy3: filters.hierarchy3,
        hierarchy4: filters.hierarchy4,
        hierarchy5: filters.hierarchy5,
        hierarchy6: filters.hierarchy6,
        hierarchy7: filters.hierarchy7,
        hierarchy8: filters.hierarchy8,
        customStyleGroup: filters.customStyleGroup,
        tiers: filters.stores,
      },
      startDate: startDate ? moment(startDate).format('YYYY-MM-DD') : null,
      endDate: endDate ? moment(endDate).format('YYYY-MM-DD') : null,
      //  status: 'pending',
    };
    this.props.getMarkdowns(payload);
  };
  ////////// handling reset issues rising bcs of tabs end //////////////////
  render() {
    return (
      <div className="createMd pl-2 pr-2">
        <div className="card">
          <div>
            <h4 className="font-size-14  pad-10 markdown-heading">
              MarkSmart /<span className="font-bold color-blue">Status</span>
            </h4>
          </div>
          <hr
            className="mb-2"
            style={{
              marginTop: '-4px',
            }}
          />
          <div className="filterCol mb-4">
            <Filters
              drop
              stores
              dateRange
              onFilter={this.onFilter}
              onReset={(e) => {
                this.props.textStrHandler(true);
                this.props.onResetStatusTable(e);
              }}
              hierarchy={[
                {
                  type: 'hierarchy1',
                  isMultiple: false,
                  isMandatory: true,
                },
                {
                  type: 'hierarchy2',
                  isMultiple: true,
                  isMandatory: true,
                },
                {
                  type: 'hierarchy3',
                  isMultiple: true,
                },
                {
                  type: 'hierarchy4',
                  isMultiple: true,
                },
                {
                  type: 'hierarchy5',
                  isMultiple: true,
                },
                {
                  type: 'hierarchy6',
                  isMultiple: true,
                },
                {
                  type: 'hierarchy7',
                  isMultiple: true,
                },
                {
                  type: 'hierarchy8',
                  isMultiple: true,
                },
              ]}
              storeIsNotMandatory={true}
              customStyleGroups
              from="draft/status" ////////// handling reset issues rising bcs of tabs
            />
          </div>
        </div>
        {this.props.statusMarkdowns && this.props.statusMarkdowns.length ? (
          <div className="card pl-3 pr-3 mt-3">
            <ColSelBtn>
              {this.props.statusColumnHeaders &&
                Object.keys(this.props.statusColumnHeaders).length && (
                  <ColumnSelection
                    onHandleCheckBox={this.props.onHandleCheckBox}
                    columnHeaders={this.props.statusColumnHeaders}
                    onHandleSelect={this.props.onHandleSelect}
                    columnOptions={this.props.columnOptions}
                    selectedItems={this.props.selectedItems}
                    selLabels={this.props.selLabels}
                  />
                )}
              <div style={{ padding: '1rem', textTransform: 'capitalize' }} className="flexRow">
                {this.props.statusCounts && Object.keys(this.props.statusCounts).length ? (
                  <StatusListCount statusCounts={this.props.statusCounts} />
                ) : (
                  ''
                )}
              </div>
            </ColSelBtn>

            <DataTable
              isExpanded={true}
              isSort={true}
              // isColumnFilter={true}
              columns={this.props.columns}
              data={this.props.statusMarkdowns}
              isPagination={true}
              getColumnProps={(column) => ({
                // onClick: () => this.onColumnClick(column),
              })}
              getCellProps={(cellInfo) => ({
                onClick: () => this.onCellClick(cellInfo),
                style: {
                  backgroundColor: this.bgmCol(cellInfo),
                  color: this.txtCol(cellInfo),
                  margin: '4px 4px',
                  borderRadius: '2px',
                },
              })}
            />
          </div>
        ) : (
          <NoData description={this.props.statusTextStr} />
        )}
        <Modal
          size="sm"
          show={this.state.isComment}
          header="Add Comment"
          onHide={this.onCloseComment}
          onSaveComment={this.onSaveComment}>
          <textarea id="commentTextArea" rows="2" cols="20" onChange={this.setComment}></textarea>
        </Modal>
      </div>
    );
  }
}
const mapStateToProps = (store) => {
  return {
    // markdowns: store.draftStatusReducer.markdowns,
    // getColumnHeaders: store.draftStatusReducer.columnHeaders,
    statusMarkdowns: store.draftStatusReducer.statusMarkdowns,
    statusColumnHeaders: store.draftStatusReducer.statusColumnHeaders,
    getStatusOptions: store.draftStatusReducer.statusOptions,
    // selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    // selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    // selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    // selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
    // selectedHierarchy5: store.filtersReducer.selectedHierarchy5,
    // selectedHierarchy6: store.filtersReducer.selectedHierarchy6,
    // selectedHierarchy7: store.filtersReducer.selectedHierarchy7,
    // selectedHierarchy8: store.filtersReducer.selectedHierarchy8,
    // selectedStores: store.filtersReducer.selectedStores,
    // startDate: store.filtersReducer.startDate,
    // endDate: store.filtersReducer.endDate,
  };
};
const mapActionToProps = {
  onResetStatusTable: DraftAction.onResetStatusTable,
};
export default connect(mapStateToProps, mapActionToProps)(Status);

const ColSelBtn = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;

  .flexRow {
    display: flex;
    flex-direction: row;
  }
`;
