import React, { Component } from 'react';
import { connect } from 'react-redux';
import Filters from '../../services/filters';
import CardComponent from '../../services/cardComponent/cardComponent';
import DataTable from '../../services/reactTable';
import ColumnSelection from '../columnSelection';
import styled from 'styled-components';
import plannedReportsAction from '../../actions/plannedReportsAction';
import _ from 'lodash';
import LoadingOverlay from '../../utils/Loader';

import card1Image from '../../images/card1Image.png';
import card2Image from '../../images/card2Image.png';
import card3Image from '../../images/card3Image.png';
import card4Image from '../../images/card4Image.png';
import { toUnit } from '../../helper/formatter';

const options = [
  { label: 'Sell Through', value: '1' },
  { label: 'Margin %', value: '2' },
  { label: 'Mark Down %', value: '3' },
];
const attributes = ['Margin', 'Sales', 'Inventory'];

class planedReport extends Component {
  constructor(props) {
    super(props);
    this.state = {
      filters: [
        {
          type: 'hierarchy1',
          isMandatory: true,
          isMultiple: false,
        },
        {
          type: 'hierarchy2',
          isMandatory: true,
          isMultiple: true,
        },
        {
          type: 'hierarchy3',
          isMandatory: true,
          isMultiple: true,
        },
        {
          type: 'hierarchy4',
          isMandatory: true,
          isMultiple: true,
        },
        {
          type: 'hierarchy5',
          isMandatory: true,
          isMultiple: true,
        },
        {
          type: 'hierarchy6',
          isMandatory: false,
          isMultiple: true,
        },
      ],

      selectedItems: [],
      firstCardText: 'Avg Markdown',
      secondCardText: 'Sell through %',
      thirdCardText: 'Margin %',
      fourthCardText: 'Quantity Sold',
      firstCardFirstValue: '12%',
      firstCardSecondValue: '24%',
      secondCardFirstValue: '16%',
      secondCardSecondValue: '20%',
      thirdCardFirstValue: '6%',
      thirdCardSecondValue: '10%',
      fourthCardFirstValue: '345',
      fourthCardSecondValue: '3,457',
      cardWeekText: 'this Week',
      cardMonthText: 'this Month',
    };
  }
  onHandleSelect = (selectValues) => this.setState({ selectValues });
  onFilter = (filters) => {
    let req = {
      filters: {
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
        hierarchy5: this.props.selectedHierarchy5,
        hierarchy6: this.props.selectedHierarchy6,
      },
      startDate: this.props.startDate,
      endDate: this.props.endDate,
      week: this.props.weeks,
    };
    this.props.fetchPlannedReports(req);
  };
  coldef = () => {
    if (this.props.getPlannedTableData.length) {
      let columns = [
        {
          Header: 'SKU',
          accessor: 'style',
          disableGroupBy: true,
          //   width: 50,
          align: 'center',
          enableRowSpan: true,
        },
        {
          Header: 'Metrics',
          accessor: 'metrices',
          disableGroupBy: true,
          //   width: 50,
          align: 'left',
        },
      ];
      Object.keys(this.props.getPlannedTableData[0]).forEach((i) => {
        if (!columns.map((c) => c.accessor).includes(i)) {
          columns.push({
            Header: i,
            accessor: i,
            disableGroupBy: true,
            Cell: (props) => <div> {toUnit(props)} </div>,
          });
        }
      });
      return columns;
    } else return [];
  };
  render() {
    let { getPlannedMetricOptions, getPlannedTableData } = this.props;
    return (
      <div className="planned-report pl-2 pr-2">
        <div className="card mb-4">
          <div>
            <h4 className="font-size-14  pad-10 markdown-heading">
              Markdown Optimization /
              <span className="font-bold color-blue">Actual Vs Planned Reports</span>
            </h4>
          </div>
          <hr
            className="mb-2"
            style={{
              marginTop: '-4px',
            }}
          />
          <Filters onFilter={this.onFilter} drop stores dateRange hierarchy={this.state.filters} />
          <hr className="dotted-line"></hr>
        </div>
        <LoadingOverlay loader={this.props.overlayLoaderState} spinner>
          <div className="row" style={{ padding: '1px 18px' }}>
            <div className="col-md-3 col-sm-3 col-xs-6 ">
              <CardComponent
                cardImage={card1Image}
                cardText={this.state.firstCardText}
                cardValue={this.state.firstCardFirstValue}
                cardValueSecond={this.state.firstCardSecondValue}
                cardWeekText={this.state.cardWeekText}
                cardMonthText={this.state.cardMonthText}
                cardStyles={{
                  cardClass: 'card-border1',
                  cardValue: 'card1-value',
                  cardImg: 'card1-img',
                  cardValueSecond: 'card-value-second',
                  cardSubHeading: 'card-subHeading',
                }}
              />
            </div>
            <div className="col-md-3 col-sm-3 col-xs-6">
              <CardComponent
                cardImage={card2Image}
                cardText={this.state.secondCardText}
                cardValue={this.state.secondCardFirstValue}
                cardValueSecond={this.state.secondCardSecondValue}
                cardWeekText={this.state.cardWeekText}
                cardMonthText={this.state.cardMonthText}
                cardStyles={{
                  cardClass: 'card-border2',
                  cardValue: 'card1-value',
                  cardImg: 'card2-img',
                  cardValueSecond: 'card-value-second',
                  cardSubHeading: 'card-subHeading',
                }}
              />
            </div>
            <div className="col-md-3 col-sm-3 col-xs-6 ">
              <CardComponent
                cardImage={card3Image}
                cardText={this.state.thirdCardText}
                cardValue={this.state.thirdCardFirstValue}
                cardValueSecond={this.state.thirdCardSecondValue}
                cardWeekText={this.state.cardWeekText}
                cardMonthText={this.state.cardMonthText}
                cardStyles={{
                  cardClass: 'card-border3',
                  cardValue: 'card1-value',
                  cardImg: 'card3-img',
                  cardValueSecond: 'card-value-second',
                  cardSubHeading: 'card-subHeading',
                }}
              />
            </div>
            <div className="col-md-3 col-sm-3 col-xs-6 ">
              <CardComponent
                cardImage={card4Image}
                cardText={this.state.fourthCardText}
                cardValue={this.state.fourthCardFirstValue}
                cardValueSecond={this.state.fourthCardSecondValue}
                cardWeekText={this.state.cardWeekText}
                cardMonthText={this.state.cardMonthText}
                cardStyles={{
                  cardClass: 'card-border4',
                  cardValue: 'card1-value',
                  cardImg: 'card4-img',
                }}
              />
            </div>
          </div>
          <div className="card CalendarMain promoCalendarDiv1 mt-3">
            <ColSelBtn>
              <ColumnSelection
                tab="draft"
                options={getPlannedMetricOptions}
                onHandleSelect={this.onHandleSelect}
                selectedItems={this.state.selectedItems}
                attributes={attributes}
                contentRenderer={true}
              />
              <div style={{ padding: '1rem' }}>
                <button className="btn btn-primary">
                  Download
                  <span>
                    <i class="fa fa-download btn-padding" aria-hidden="true"></i>
                  </span>
                </button>
              </div>
            </ColSelBtn>
            <div className="pad-15">
              <DataTable columns={this.coldef()} data={getPlannedTableData} isPagination={false} />
            </div>
          </div>
        </LoadingOverlay>
      </div>
    );
  }
}
const mapStateToProps = (store) => {
  return {
    overlayLoaderState: store.loaderReducer.overlayLoaderState,
    getPlannedTableData: store.plannedReportReducer.plannedReportsTableData,
    getPlannedMetricOptions: store.plannedReportReducer.metricOptions.draft,
    selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
    selectedHierarchy5: store.filtersReducer.selectedHierarchy5,
    selectedHierarchy6: store.filtersReducer.selectedHierarchy6,
    startDate: store.filtersReducer.startDate,
    endDate: store.filtersReducer.endDate,
    weeks: store.filtersReducer.weeks,
  };
};
const mapActionsToProps = {
  fetchPlannedReports: plannedReportsAction.fetchPlannedReports,
};

export default connect(mapStateToProps, mapActionsToProps)(planedReport);
// export default planedReport;
const ColSelBtn = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;
