import React, { Component } from 'react';
import Select from 'react-select';
import { InputGroup, FormControl } from 'react-bootstrap';
import cloneDeep from 'clone-deep';
import _ from 'lodash';
// import { toast } from 'react-toastify';
// import "./index.css"

let scenarioCheck = false;

class PlanSave extends Component {
  constructor(props) {
    super(props);
    this.state = {
      planName: '',
      scenarioName: '',
      showErrorMessage: false,
      tempArr: [],
    };
  }

  validateInput = (inputs) => {
    if (this.props.activeTab !== 'Comparison') {
      return true;
    } else {
      let value = inputs.map((obj) => {
        if (obj.value) {
          return obj.value;
        }
        return obj.label;
      });
      return new Set(value).size === value.length;
    }
  };
  render() {
    let scenario =
      this.props.editPlanDetails[0] && this.props.editPlanDetails[0].cadence_data[0].scenario;
    let tabsData = cloneDeep(this.props.data);
    let tempArr = this.state.tempArr;
    if (this.props.activeTab === 'Comparison' && !scenarioCheck) {
      tempArr = this.props.comparsionSelectedData.map((row) => {
        return Object.assign({}, row, {
          label: row.scenarios,
          value: '',
        });
      });
    }
    if (this.props.activeTab !== 'Comparison' && !scenarioCheck) {
      let data = tabsData[this.state.activeIndex] ? tabsData[this.state.activeIndex][0] : {};
      data.label = this.props.activeTab;
      data.value = '';
      tempArr = [data];
      // data.forEach((label) => {
      //   if (label !== 'Optimal' && label !== 'Comparison') {
      //     tempArr.push({
      //       label: label,
      //       value: '',
      //     });
      //   }
      // });
    }

    return (
      <div>
        <div className="row">
          <div className="col-md-6">
            {' '}
            <p
              className="font-size-14 mb-0 ml-3"
              style={{ fontWeight: 'bold', whiteSpace: 'nowrap' }}>
              <span className="required">*</span>Markdown Plan Name
            </p>
          </div>
          <div className="col-md-6">
            <InputGroup className="mb-3">
              <FormControl
                disabled={this.props.isEdit}
                aria-label="planName"
                aria-describedby="basic-addon2"
                style={{ padding: '18px' }}
                value={
                  this.props.isEdit
                    ? this.props.editPlanDetails[0] && this.props.editPlanDetails[0].title
                    : this.state.planName
                }
                type="text"
                onChange={(e) => {
                  this.setState({ planName: e.target.value });
                }}
              />
            </InputGroup>
          </div>
        </div>
        {tempArr.map((obj, index) => {
          return (
            <div className="row" key={index}>
              <div className="col-md-6">
                {' '}
                <p className="font-size-14 mb-0 ml-3" style={{ fontWeight: 'bold' }}>
                  {obj.label}
                </p>
              </div>
              <div className="col-md-6">
                <InputGroup className="mb-3">
                  <FormControl
                    aria-label="scenarioName"
                    aria-describedby="basic-addon2"
                    style={{ padding: '18px' }}
                    value={this.props.isEdit ? scenario : obj.value}
                    type="text"
                    onChange={(e) => {
                      scenarioCheck = true;
                      obj.value = e.target.value;
                      this.setState({
                        showErrorMessage: false,
                        tempArr,
                      });
                      // this.setState({ [obj.value]: e.target.value });
                    }}
                  />
                </InputGroup>
              </div>
            </div>
          );
        })}
        {this.state.showErrorMessage && (
          <div style={{ color: 'red', textAlign: 'center' }}>Scenario name should be unique</div>
        )}
        <div className="saveBtn">
          <button
            className="btn btn-primary biconbuttonFilter"
            style={{ width: '15%' }}
            disabled={this.props.isEdit || !this.state.planName}
            onClick={() => {
              if (this.validateInput(tempArr)) {
                this.props.onSave(this.state.planName, tempArr);
                scenarioCheck = false;
                this.setState({
                  planName: '',
                  tempArr: [],
                  showErrorMessage: false,
                });
              } else {
                this.setState({
                  showErrorMessage: true,
                });
              }
            }}>
            Save
          </button>
        </div>
      </div>
    );
  }
}

export default PlanSave;
