import axios from '../utils/axios';
import { config } from '../utils/config';
import _ from 'lodash';

const filterActions = {
  getDrops: () => {
    return (dispatch) => {
      axios({
        url: `${config.createMarkdownURL}/getSeasonFilters`,
        method: 'GET',
      })
        .then((resp) => {
          dispatch({
            type: 'SET_DROPS',
            payload: resp.data.data,
          });
        })
        .catch((err) => {
          console.log(err);
        });
    };
  },

  getHierarchyOptions: (data) => {
    return (dispatch) => {
      axios({
        url: `${config.createMarkdownURL}/getHierarchyFilterData/`,
        method: 'POST',
        data: {
          filters: data.filters,
          hierarchyType: data.hierarchyType,
          // allowOnlyActiveProducts: data.allowInactiveProducts ? data.allowInactiveProducts : false,
        },
      })
        .then((resp) => {
          dispatch({
            type: `SET_${data.hierarchyType}`,
            payload: resp.data.data,
          });
        })
        .catch((err) => {
          console.log(err);
        });
    };
  },
  fetchStyleGroups: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.createMarkdownURL}/getCustomStyleGroup/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          dispatch({
            type: 'SET_STYLE_GROUPS',
            payload: resp.data.data,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return resp.data.data;
        })
