import React from 'react';
import styled from 'styled-components';
import _ from 'lodash';
import numeral from 'numeral';

const AnalysisTable = ({ tableData, tableData2, isEdit, editFrom }) => {
  tableData = tableData || [];
  tableData2 = tableData2 || [];
  const getHeader = () => {
    if (isEdit && !_.isEmpty(tableData2)) {
      let headers = ['', 'Previous', 'Edited'];
      return headers.map((header, index) => {
        return (
          <th key={index} style={{ backgroundColor: '#e8eef2' }}>
            {header}
          </th>
        );
      });
    }
  };

  return (
    <Styles>
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <table>
          {editFrom && editFrom !== 'notEditModal' ? ( // horizontal table for edit popup
            <tbody>
              <tr>
                {isEdit && !_.isEmpty(tableData) && !_.isEmpty(tableData2) && (
                  <th className="cellAlign" style={{ backgroundColor: '#e8eef2' }} />
                )}
                {tableData.length && tableData.length > 0
                  ? tableData.map((cell, index) => {
                      return (
                        <th className="cellAlign" style={{ backgroundColor: '#e8eef2' }}>
                          {cell.style_id}
                        </th>
                      );
                    })
                  : null}
              </tr>
              <tr>
                {isEdit && !_.isEmpty(tableData) && !_.isEmpty(tableData2) && (
                  <td className="cellData">Previous</td>
                )}
                {tableData.length && tableData.length > 0
                  ? tableData.map((cell, index) => {
                      let styleDesc = cell.style_desc;
                      let sellThroughVal = tableData[0].style_desc.toString();
                      if (!sellThroughVal.includes('%')) {
                        let value =
                          index < 4
                            ? numeral(cell.style_desc).format('0.00')
                            : numeral(cell.style_desc).format('0,0');
                        styleDesc = cell.style_id.includes('%')
                          ? `${value}%`
                          : cell.style_id.includes('$')
                          ? `$${value}`
                          : value;
                      }
                      return <td className="cellData">{styleDesc}</td>;
                    })
                  : null}
                {/* <td colSpan="5">No data found</td> */}
              </tr>
              {isEdit && !_.isEmpty(tableData2) ? (
                <tr>
                  {isEdit && !_.isEmpty(tableData) && !_.isEmpty(tableData2) && (
                    <td className="cellData">Edited</td>
                  )}
                  {tableData.length && tableData.length > 0
                    ? tableData.map((cell, index) => {
                        let styleDescEdited =
                          isEdit && !_.isEmpty(tableData2) ? tableData2[index].style_desc : null;
                        let sellThroughVal = tableData[0].style_desc.toString();
                        let editedsellThroughVal =
                          isEdit && !_.isEmpty(tableData2)
                            ? tableData2[0].style_desc.toString()
                            : null;
                        if (!sellThroughVal.includes('%')) {
                          let editedValue =
                            editedsellThroughVal &&
                            (index < 4
                              ? numeral(tableData2[index].style_desc).format('0.00')
                              : numeral(tableData2[index].style_desc).format('0,0'));

                          styleDescEdited =
                            editedsellThroughVal &&
                            (tableData2[index].style_id.includes('%')
                              ? `${editedValue}%`
                              : tableData2[index].style_id.includes('$')
                              ? `$${editedValue}`
                              : editedValue);
                        }
                        return <td className="cellData">{styleDescEdited}</td>;

                        // return <td className="cellData">{tableData2[index].style_desc}</td>;
                      })
                    : null}
                </tr>
              ) : null}
            </tbody>
          ) : (
            <tbody>
              <tr>{getHeader()}</tr>
              {tableData.length && tableData.length > 0 ? (
                tableData.map((cell, index) => {
                  let styleDesc = cell.style_desc;
                  let styleDescEdited =
                    isEdit && !_.isEmpty(tableData2) ? tableData2[index].style_desc : null;

                  let sellThroughVal = tableData[0].style_desc.toString();
                  let editedsellThroughVal =
                    isEdit && !_.isEmpty(tableData2) ? tableData2[0].style_desc.toString() : null;

                  if (!sellThroughVal.includes('%')) {
                    let value =
                      index < 4
                        ? numeral(cell.style_desc).format('0.00')
                        : numeral(cell.style_desc).format('0,0');

                    let editedValue =
                      editedsellThroughVal &&
                      (index < 4
                        ? numeral(tableData2[index].style_desc).format('0.00')
                        : numeral(tableData2[index].style_desc).format('0,0'));

                    styleDesc = cell.style_id.includes('%')
                      ? `${value}%`
                      : cell.style_id.includes('$')
                      ? `$${value}`
                      : value;

                    styleDescEdited =
                      editedsellThroughVal &&
                      (tableData2[index].style_id.includes('%')
                        ? `${editedValue}%`
                        : tableData2[index].style_id.includes('$')
                        ? `$${editedValue}`
                        : editedValue);
                  }

                  return (
                    <tr key={index}>
                      <td className="cellAlign" style={{ backgroundColor: '#e8eef2' }}>
                        {cell.style_id}
                      </td>
                      <td className="cellData">{styleDesc}</td>
                      {isEdit && !_.isEmpty(tableData2) ? (
                        <td className="cellData">{styleDescEdited}</td>
                      ) : null}
                    </tr>
                  );
                })
              ) : (
                <tr>
                  <td colSpan="5">Loading...</td>
                </tr>
              )}
            </tbody>
          )}
        </table>
      </div>
    </Styles>
  );
};

export default AnalysisTable;

const Styles = styled.table`
  font-family: arial, sans-serif;
  border-collapse: collapse;
  width: 100%;

  td,
  th {
    border: 1px solid #dddddd;
    text-align: center;
    padding: 8px;
  }
  tr {
    font-size: 15px;
    line-height: 4rem;
    font-size: 15px;
    color: #3c4148;
    font-weight: 500;
    // background-color: #e8eef2;
  }
`;
