import React, { Component, Fragment } from "react";
import Slider from "react-input-slider";

class AutoPromoCreationInputs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: { x: 0 }
    };
  }

  componentDidMount() {
    this.setState({
      data: { x: this.props.data }
    });
  }

  componentWillReceiveProps(props) {
    this.setState({
      data: { x: props.data }
    });
  }

  render() {
    return (
      <Fragment>
        <Slider
          axis="x"
          xstep={1}
          xmin={0}
          xmax={100}
          x={this.state.data.x}
          onChange={({ x }) => {
            this.setState({
              data: { x: parseFloat(x.toFixed(2)) }
            });
            this.props.setValue(x);
          }}
        />
      </Fragment>
    );
  }
}

export default AutoPromoCreationInputs;
