import React, { Component } from "react";
import Select from "react-select";
import { InputGroup, FormControl } from "react-bootstrap";
// import "./index.css"
class PlanSave extends Component {
  constructor(props) {
    super(props);
    this.state = {
      planName: "",
      scenarioName: "",
    };
  }
  render() {
    return (
      <div>
        <div className="row mt-3">
          <div className="col-md-4">
            {" "}
            <p
              className="font-size-14 mb-0 ml-3"
              style={{ fontWeight: "bold" }}
            >
              <span className="required">*</span>Markdown Plan Name
            </p>
          </div>
          <div className="col-md-6">
            <InputGroup className="mb-3">
              <FormControl
                aria-label="planName"
                aria-describedby="basic-addon2"
                style={{ padding: "18px" }}
                value={this.state.planName}
                type="text"
                onChange={(e) => {
                  this.setState({ planName: e.target.value });
                }}
              />
            </InputGroup>
          </div>
        </div>
        <div className="row">
          <div className="col-md-4">
            {" "}
            <p
              className="font-size-14 mb-0 ml-3"
              style={{ fontWeight: "bold" }}
            >
              <span className="required">*</span>Scenario 01 Name
            </p>
          </div>
          <div className="col-md-6">
            <InputGroup className="mb-3">
              <FormControl
                aria-label="scenarioName"
                aria-describedby="basic-addon2"
                style={{ padding: "18px" }}
                value={this.state.scenarioName}
                type="text"
                onChange={(e) => {
                  this.setState({ scenarioName: e.target.value });
                }}
              />
            </InputGroup>
          </div>
        </div>
        <div className="row">
          <div className="col-md-4">
            {" "}
            <p
              className="font-size-14 mb-0 ml-3"
              style={{ fontWeight: "bold" }}
            >
              <span className="required">*</span>Scenario 02 Name
            </p>
          </div>
          <div className="col-md-6">
            <InputGroup className="mb-3">
              <FormControl
                aria-label="scenarioName"
                aria-describedby="basic-addon2"
                style={{ padding: "18px" }}
                value={this.state.scenarioName}
                type="text"
                onChange={(e) => {
                  this.setState({ scenarioName: e.target.value });
                }}
              />
            </InputGroup>
          </div>
        </div>
        <div className="row">
          <div className="col-md-4">
            {" "}
            <p
              className="font-size-14 mb-0 ml-3"
              style={{ fontWeight: "bold" }}
            >
              <span className="required">*</span>Scenario 03 Name
            </p>
          </div>
          <div className="col-md-6">
            <InputGroup className="mb-3">
              <FormControl
                aria-label="scenarioName"
                aria-describedby="basic-addon2"
                style={{ padding: "18px" }}
                value={this.state.scenarioName}
                type="text"
                onChange={(e) => {
                  this.setState({ scenarioName: e.target.value });
                }}
              />
            </InputGroup>
          </div>
        </div>
        <div className="saveBtn">
          <button
            className="btn btn-primary biconbuttonFilter"
            style={{ width: "15%" }}
          >
            Save
          </button>
        </div>
      </div>
    );
  }
}

export default PlanSave;
