import React, { Component } from "react";
import moment from "moment";

class calendarWrapperToggleComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      styleHandlePrev: {
        cursor: "pointer"
      },
      styleHandleNext: {
        cursor: "pointer"
      },
      styleGetPrev: {
        bottom: "13%",
        left: "-0.2rem",
        cursor: "pointer"
      },
      styleGetNext: {
        bottom: "13%",
        right: "-0.2rem",
        cursor: "pointer"
      },
      loader: false,
      calendarDataArr: [],
    };
    this.Month = [
      "Jan",
      "Feb",
      "Mar",
      "Apr",
      "May",
      "Jun",
      "Jul",
      "Aug",
      "Sep",
      "Oct",
      "Nov",
      "Dec"
    ];
  }

  //250 lines
  componentDidUpdate(oldProps, oldState) {
    let { calendarDataPerYear } = this.props;

    if (oldProps.calendarDataPerYear !== calendarDataPerYear) {
      if(this.props.markdownlinkShow === 1){
        this.getToggleStyle();
      }else if(this.props.showToggleContinious){
        this.getPromoYearStyle()
      }
    }
  }

  getToggleStyle = () => {
    let {
