chartjs.pluginService.register({
  afterDatasetDraw: function (chart, args, options) {
    if (args.index === 1 && chart.options.lineDash) {
      // if (chart.options.lineDash) {
      args.meta.data.forEach(function (element) {
        var borderWidth = 3;
        var ctx = chart.ctx;
        var vm = element._view;
        var half = vm.width / 2;
        var left = vm.x - half;
        var right = vm.x + half;
        var top = vm.y;
        var width = right - left;
        // var height = chart.chartArea.bottom - top + borderWidth / 2 - 1;
        ctx.beginPath();
        ctx.lineWidth = borderWidth;
        ctx.strokeStyle = vm.borderColor;
        ctx.setLineDash(chart.options.lineDash);
        // ctx.moveTo(left, top);
        // ctx.lineTo(left, top + height);
        ctx.moveTo(left, top);
        ctx.lineTo(left + width, top);
        ctx.moveTo(left + width, top);
        // ctx.lineTo(left + width, top + height);
        ctx.stroke();
        ctx.save();
      });
    }
  },
});

