import React from 'react';
import { formatThousands } from '../../helper/formatter';
const InventoryInfo = (props) => {
  return (
    <div>
      <h5 className="font-size-14 font-bold" style={{ paddingLeft: '12px' }}>
        Inventory Info
      </h5>{' '}
      <div className="d-flex" style={{ justifyContent: ' space-around' }}>
        {/* <div className="col-md-1 offset-md-1"> */}
        <div className="info-icon">
          <i className="inventory-icon"></i>
          {/* </div> */}
        </div>
        {/* <div className="col-md-1"> */}
        <div className="info-data">
          <p className="font-size-14 mb-0 font-normal">Total DC</p>
          <div className="info-numbers">
            {props.isEdit
              ? props.editPlanDetails[0] && formatThousands(props.editPlanDetails[0].dc_units)
              : props.totalInDc === 0
              ? 0
              : formatThousands(props.totalInDc)}
          </div>
        </div>
        {/* </div> */}
        {/* <div className="col-md-1"> */}
        <div className="info-data">
          <p className="font-size-14 mb-0 font-normal">Store On Hand</p>
          <div className="info-numbers">
            {props.isEdit
              ? props.editPlanDetails[0] && formatThousands(props.editPlanDetails[0].oh_units)
              : formatThousands(props.totalOnHand)}
          </div>
        </div>
        {/* </div> */}
        {/* <div className="col-md-1"> */}
        <div className="info-data">
          <p className="font-size-14 mb-0 font-normal">Store On Order</p>
          <div className="info-numbers">
            {props.isEdit
              ? props.editPlanDetails[0] && formatThousands(props.editPlanDetails[0].oo_units)
              : formatThousands(props.totalOnOrder)}
          </div>
        </div>
        {/* </div> */}
        {/* <div className="col-md-1"> */}
        <div className="info-data ">
          <p className="font-size-14 mb-0 font-normal">Store In Transit</p>
          <div className="info-numbers">
            {props.isEdit
              ? props.editPlanDetails[0] && formatThousands(props.editPlanDetails[0].it_units)
              : formatThousands(props.totalInTransit)}
          </div>
        </div>
        {/* </div> */}
        {/* <div className="col-md-1 offset-md-1"> */}
        <div className="info-icon">
          <i className="store-icon"></i>
        </div>
        {/* </div> */}
        {/* <div className="col-md-1"> */}
        <div className="info-data ">
          <p className="font-size-14 mb-0 font-normal ">Stores Selected</p>
          <div className="info-numbers ">
            {props.isEdit
              ? props.editPlanDetails[0] && props.editPlanDetails[0].stores.length
              : formatThousands(props.selectedStores)}
            /
            <span className="font-size-14 font-bold">
              {props.isEdit
                ? props.editPlanDetails[0] && props.editPlanDetails[0].stores.length
                : formatThousands(props.totalStores)}
            </span>
          </div>
        </div>
        {/* </div> */}
        {/* <div className="col-md-2"> */}
        <div className="info-data">
          {/* <p className="font-size-14 mb-0 font-normal ">Limit clearance to few stores</p> */}
          <button
            className="btn btn-primary"
            disabled={!props.totalStores || props.isEdit}
            style={{ height: '45%', width: '187px' }}
            onClick={() => props.onExcludeStores()}>
            Exclude Stores
          </button>
        </div>
        {/* </div> */}
      </div>
    </div>
  );
};
export default InventoryInfo;
