import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import Select from 'react-select';
import '../../Configuration/StoreConfigContainers/StoreConfig.css';
import '../../../index.css';
import DataTable from '../../../services/reactTable';
import { connect } from 'react-redux';
import StoreConfigRowData from '../StoreConfigContainers/StoreConfigRowData';
import StoreConfigActions from '../../../actions/storeConfigActions';
import _ from 'lodash';

const deptData = [
  {
    value: 3,
    label: 'testing create api for edit',
  },
  {
    value: 2,
    label: 'testing create api',
  },
  {
    value: 4,
    label: 'testing create api for edit 1',
  },
  {
    value: 1,
    label: 'test',
  },
];
export class StoreConfig extends Component {
  state = {
    storeGroup: '',
    tableData: [],
  };
  header = [
    {
      Header: 'Store Number',
      accessor: 'store_code',
      filter: 'fuzzyText',
    },
    {
      Header: 'Store Name',
      accessor: 'store_name',
      filterable: false, //This makes the column not filterable
    },
    {
      Header: 'Chain Description',
      accessor: 'chain_description',
    },
    {
      Header: 'Region Description',
      accessor: 'region_description',
    },
  ];
  componentDidMount() {
    this.props.fetchStoreGroups();
  }
  onStoreGroupChangeHandler = (val) => {
    this.setState({
      storeGroup: val,
    });
  };
  handleFilter = async () => {
    let storeGroup = this.state && this.state.storeGroup && this.state.storeGroup.value;
    let res = await this.props.getStoreGroupTableData({ Stores_groups_Id: [storeGroup] });
    this.setState({ tableData: res });
    console.log(storeGroup, 'storegrr');
  };

  clearStoreFilter = () => {
    this.setState({
      storeGroup: '',
      tableData: [],
    });
  };

  onCreateStoreGroupHandler = (isEditStoreGroup = false, data) => {
    if (isEditStoreGroup) {
      this.props.history.push(`/vis/Markdown/configuration/store/group/edit/${data}`);
    } else {
      this.props.history.push(`/vis/Markdown/configuration/store/group/create`);
    }
  };
  render() {
    console.log(this.props.storeGroups, this.props.storeGroupTableData, 'storeGroupsstoreGroups');
    let { tableData } = this.state;
    let noOfStores = tableData.map((a) => a.store_code);
    console.log(noOfStores.length, 'tableDatatableData');

    return (
      <div className="card p-2 mt-4">
        <div className="row mt-3 mb-2" style={{ justifyContent: 'space-between' }}>
          <div className="col-md-6">
            <div className="row">
              <div className="col-md-3" style={{ textAlign: 'right' }}>
                Store Group
              </div>
              <div className="col-md-4">
                <Select
                  id="store-group-select"
                  placeholder="Select Store Group"
                  options={this.props.storeGroups}
                  value={this.state.storeGroup}
                  onChange={this.onStoreGroupChangeHandler}
                />
              </div>
              <div className="col-md-4 pt-1">
                <button className="btn btn-primary" onClick={this.handleFilter}>
                  <span>
                    <i className="fa fa-filter"></i>&nbsp; Filter
                  </span>
                </button>
                <button
                  onClick={this.clearStoreFilter}
                  className="btn btn-primary  btn-filter-reset ml-2">
                  <span>
                    <i className="fa fa-repeat"></i>&nbsp; Reset
                  </span>
                </button>
              </div>
            </div>
          </div>
          {this.props.userRole !== 'Admin' && (
            <div className="col-md-2 pt-1 mr-3 text-right">
              <button className="btn btn-primary" onClick={() => this.onCreateStoreGroupHandler()}>
                Create store group
              </button>
            </div>
          )}
        </div>
        <hr />
        {!_.isEmpty(tableData) ? (
          <>
            <div className="row">
              <div className="col-md-2 offset-md-2">No of stores: {noOfStores.length}</div>
              {this.props.userRole !== 'Admin' && (
                <div className="col-md-7 text-right">
                  <button
                    className="btn btn-primary"
                    onClick={() =>
                      this.onCreateStoreGroupHandler(
                        true,
                        this.state && this.state.storeGroup && this.state.storeGroup.value
                      )
                    }>
                    Edit store group
                  </button>
                </div>
              )}
            </div>
            <div
              style={{
                height: '370px',
                width: '65%',
                margin: '1.5rem auto',
                overflow: 'auto',
              }}>
              <DataTable
                isColumnFilter={true}
                isSort={true}
                columns={this.header}
                data={tableData}
              />
            </div>
          </>
        ) : null}
      </div>
    );
  }
}

const mapStateToProps = (store) => {
  return {
    getOverlayLoaderState: store.loaderReducer.overlayLoaderState,
    getOverlayLoaderText: store.loaderReducer.loaderText,
    storeGroups: store.storeConfigReducer.storeGroups,
    storeGroupTableData: store.storeConfigReducer.storeGroupTableData,
  };
};
const mapDispatchToProps = {
  fetchStoreGroups: StoreConfigActions.fetchStoreGroups,
  getStoreGroupTableData: StoreConfigActions.getStoreGroupTableData,
  createStoreGroup: StoreConfigActions.createStoreGroup,
};
export default connect(mapStateToProps, mapDispatchToProps)(withRouter(StoreConfig));
