import React, { Component } from "react";
import { Line } from "react-chartjs-2";
import "chartjs-plugin-dragdata";
import "chartjs-plugin-annotation";

class LineChart extends Component {
  render() {
    let lineOption = {
        tooltips: {
            callbacks: {
                label: function(tooltipItem, data) {
                    return `${(tooltipItem && tooltipItem.yLabel).toFixed(0).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")}`;
                }
            }
        },
      scaleShowVerticalLines: false,
      hover: {
        intersect: false
      },
      elements: {
        line: {
          tension: 0,
          fill: false
        },
        point: {
          radius: 0
        }
      },
      plugins: {
        datalabels: {
          display: false
        }
      },
      scales: {
        xAxes: [
          {
            barPercentage: 1.0,
            gridLines: {
              display: false
            },
            scaleLabel: {
              display: true,
              labelString: 'Time Period',
              fontStyle: "bold",
              fontSize: 13,
              fontColor: "#45677b"
            },
            ticks: {
              // autoSkip: false,
              maxTicksLimit: 15,
              fontSize: 10,
              opacity: 0.7,
              display: true,
              callback: function (value, index, values) {
               return value.toFixed(2).replace(/[.,]00$/, "");
              },
            }
          }
        ],
        yAxes: [
          {
            offset: true,
            gridLines: {
              display: true,
              beginAtZero: true
            },
            scaleLabel: {
              display: true,
              labelString: this.props.yLabelName,
              fontStyle: "bold",
              fontSize: 13,
              fontColor: "#45677b"
            },
            ticks: {
                // autoSkip: false,
                maxTicksLimit: 15,
                fontSize: 10,
                opacity: 0.7,
                display: true,
                callback: function (value, index, values) {
                  var no = value;
                  if (parseFloat(value) / 1000000 >= 1) {
                    //add m
                    no = parseFloat((value) / 1000000).toFixed(0) + 'M'
                  }
                  else if (parseFloat(value) / 1000 >= 1) {
                    //add k
                    no = (parseFloat(value) / 1000).toFixed(0) + 'K'
                  }
                  return no;
                },
              }

          }
        ]
      },
      legend: {
        display: false,
        position: "bottom",
        labels: {
          boxWidth: 0
        },
        fontFamily: "poppins",
        fontSize: "13px"
      },
      responsive: true,
      maintainAspectRatio: true
    };

    return (
      <div style={this.props.height && { height: this.props.height }}>
        <Line
          data={this.props.data || []}
          options={lineOption}
          width={500}
          height={this.props.height || 150}
          responsive={true}
        />
      </div>
    );
  }
}

export default LineChart;
